methods.method:
function Constructor(argobj)
                {
					var oCurObj = CLO[argobj.pid];
					var jCurObjDiv = $(oCurObj.div);
					oCurObj.data =
					{
						aPath: [],
						sDisplayType: oCurObj.jxParams.find("display_type").text(),
						sSlideId: "",
						sChapterId: "",
						sScope: oCurObj.jxParams.children("display_scope").text(),
						sState: "enabled",
						bNaviStrict: (oCurObj.jxParams.find("allow_link").text()=="strict"),
						bNaviLockedByAction: false,
						bMouseOver: (oCurObj.jxParams.find("allow_mouseover").text()!="never"),
						bLink: (oCurObj.jxParams.find("allow_link").text()!="never"),
						sSoundClick: oCurObj.jxParams.children("sound_click").text(),
						sSoundOver: oCurObj.jxParams.children("sound_over").text(),
						jRail: jCurObjDiv.find("[cl-role='progress-rail']"),
						jTemplateV: jCurObjDiv.find("#" + argobj.pid + "_TEMPLATE_V"),
						jTemplateC: jCurObjDiv.find("#" + argobj.pid + "_TEMPLATE_C"),
						jTemplateU: jCurObjDiv.find("#" + argobj.pid + "_TEMPLATE_U")
					};
					if(oCurObj.data.sSoundClick=="custom")
					{
						oCurObj.data.sSoundClickFile = oCurObj.jxParams.children("sound_click_file_custom").text();
					}
					if(oCurObj.data.sSoundOver=="custom")
					{
						oCurObj.data.sSoundOverFile = oCurObj.jxParams.children("sound_over_file_custom").text();
					}
					if(!CL.oPath.bAlternative || oCurObj.data.sScope!="chapter")
					{
						oCurObj.data.aPath = CL.oPath._GetSlideList();
					if(oCurObj.data.sDisplayType=="icons")
					{
						var jElem;
						for(var i=0; i<oCurObj.data.aPath.length; i++)
						{
								jElem = oCurObj.data.jTemplateU.clone(true).appendTo(oCurObj.data.jRail).show().removeClass("cl-progress-elem-template").css({ "display": "inline-block" }).attr({ "cl-id": oCurObj.data.aPath[i] });
							if(oCurObj.data.bMouseOver && !CL.bTouch)
							{
									jElem.on("mouseover", function (e) { CLO[argobj.pid].CallMethod({ sMethod: "Over", oMethodArgs: { evt: e, elem: this } }) }).on("mouseout", function (e) { CLO[argobj.pid].CallMethod({ sMethod: "Out", oMethodArgs: { evt: e, elem: this } }) });
							}
							if(oCurObj.data.bLink)
							{
									jElem.addClass("cl-linked").on("click", function (e) { CLO[argobj.pid].CallMethod({ sMethod: "Navigate", oMethodArgs: { evt: e, elem: this } }) });
							}
						}
					}
					else
					{
						$(oCurObj.div).find(".cl-bar").width(0);
					}
					}
					var jCounter = $(oCurObj.div).find(".cl-progress-counter");
					if(jCounter.length!=0)
					{
						jCounter.html(jCounter.html().split("{N}").join('<span class="cl-slide-number" cl-role="slide-number">0</span>').split("{Z}").join('<span class="cl-slide-total" cl-role="slide-total">' + oCurObj.data.aPath.length + '</span>'));
						oCurObj.data.jSlideNumber = jCounter.find("[cl-role='slide-number']");
						oCurObj.data.jSlideTotal = jCounter.find("[cl-role='slide-total']");
					}
					if($(oCurObj.div).find(".cl-progress-slidename-name").length!=0)
					{
						oCurObj.data.jSlideName = $(oCurObj.div).find(".cl-progress-slidename-name");
					}
					CL.Event.Subscribe({ aList: [ { sId: argobj.pid, sEvtName: "EVENT_SLIDE_OPENED" }, { sId: argobj.pid, sEvtName: "EVENT_SLIDE_COMPLETE" }, { sId: argobj.pid, sEvtName: "EVENT_NAVIGATION" } ] });
                    return true;
                }

function HandleEvt(argobj)
                {
					var oCurObj = CLO[argobj.pid];
					if(oCurObj==null) return false;
					if(oCurObj.div==null) return false;
					switch(argobj.evt)
					{
						case "EVENT_SLIDE_OPENED":
						{
							if(oCurObj.data.sSlideId!=CLZ.sCurrentSlideId)
							{
								oCurObj.data.sSlideId = CLZ.sCurrentSlideId;
								oCurObj.data.bNaviLockedByAction = false;
								oCurObj.CallMethod({ sMethod: "Update" });
							}
							oCurObj.CallMethod({ sMethod: ((oCurObj.data.bNaviStrict || oCurObj.data.bNaviLockedByAction) ? "DisableAccess" : "EnableAccess") });
							break;
						}
						case "EVENT_SLIDE_COMPLETE":
						{
							oCurObj.CallMethod({ sMethod: ((oCurObj.data.bNaviLockedByAction) ? "DisableAccess" : "EnableAccess") });
							break;
						}
						case "EVENT_NAVIGATION":
						{
							if(argobj.sTargets!=null)
							{
								if(argobj.sTargets=="progress" || argobj.sTargets=="all")
								{
									switch(argobj.sAction)
									{
										case "off":
										{
											oCurObj.data.bNaviLockedByAction = true;
											oCurObj.CallMethod({ sMethod: "DisableAccess" });
											break;
										}
										case "on":
										default:
										{
											oCurObj.data.bNaviLockedByAction = false;
											oCurObj.CallMethod({ sMethod: "EnableAccess" });
											break;
										}
									}
								}
							}
							break;
						}
					}
					return true;
                }

function DisableAccess(argobj)
			{
				$(CLO[argobj.pid].div).find(".cl-container").addClass("cl-disabled");
				CLO[argobj.pid].data.sState = "disabled";
				return true;
			}

function EnableAccess(argobj)
			{
				$(CLO[argobj.pid].div).find(".cl-container").removeClass("cl-disabled");
				CLO[argobj.pid].data.sState = "enabled";
				return true;
			}

function UpdateProgress(argobj)
                {
					var oCurObj = CLO[argobj.pid];
					var jCurObjDiv = $(oCurObj.div);
					var iCurrent = -1;
					var iNumber = -1;
					if(!CL.oPath.bAlternative || oCurObj.data.sScope!="chapter")
					{
						iNumber = CL.oPath._GetSlideNumber();
					if(oCurObj.data.sDisplayType=="icons")
					{
						var jCell;
						for(var i=0; i<oCurObj.data.aPath.length; i++)
						{
								jCell = $(oCurObj.div).find("[cl-id='" + oCurObj.data.aPath[i] + "']");
							if(oCurObj.data.aPath[i]==CLZ.sCurrentSlideId)
							{
								if(jCell.hasClass("cl-progress-img"))
								{
										jCell.removeClass("cl-progress-v cl-progress-u").addClass("cl-progress-c").attr({ "src": oCurObj.data.jTemplateC.attr("src") });
								}
								else
								{
									jCell.removeClass("cl-progress-v cl-progress-v-var cl-progress-u cl-progress-u-var").addClass("cl-progress-c cl-progress-c-var");
								}
								if(CLO[argobj.pid].data.bLink)
								{
									jCell.removeClass("cl-linked").unbind("click");
								}
								iCurrent = i;
							}
							else if(CL.SCO.IsVisited({ sBy: "slideid", sSlideId: oCurObj.data.aPath[i] }))
							{
								if(jCell.hasClass("cl-progress-img"))
								{
										jCell.removeClass("cl-progress-c cl-progress-u").addClass("cl-progress-v").attr({ "src": oCurObj.data.jTemplateV.attr("src") });
								}
								else
								{
									jCell.removeClass("cl-progress-c cl-progress-c-var cl-progress-u cl-progress-u-var").addClass("cl-progress-v cl-progress-v-var");
								}
								if(CLO[argobj.pid].data.bLink && !jCell.hasClass("cl-linked"))
								{
									jCell.addClass("cl-linked").click(function (e) { CLO[argobj.pid].CallMethod({ sMethod: "Navigate", oMethodArgs: { evt: e, elem: this } }) });
								}
							}
							else
							{
								if(jCell.hasClass("cl-progress-img"))
								{
										jCell.removeClass("cl-progress-c cl-progress-v").addClass("cl-progress-u").attr({ "src": oCurObj.data.jTemplateU.attr("src") });
								}
								else
								{
									jCell.removeClass("cl-progress-c cl-progress-c-var cl-progress-v cl-progress-v-var").addClass("cl-progress-u cl-progress-u-var");
								}
								if(CLO[argobj.pid].data.bLink)
								{
									if(((CLZ.bStrictOrder && i<=iCurrent+1) || !CLZ.bStrictOrder) && !jCell.hasClass("cl-linked"))
									{
										jCell.addClass("cl-linked").click(function (e) { CLO[argobj.pid].CallMethod({ sMethod: "Navigate", oMethodArgs: { evt: e, elem: this } }) });
									}
								}
							}
						}
					}
					else
					{
						$(oCurObj.div).find(".cl-bar").width(Math.round(iNumber/oCurObj.data.aPath.length*100) + "%");
					}
					}
					else if(CL.oPath.bAlternative && oCurObj.data.sScope=="chapter")
					{
						var sCurrentChapterId = CL.oPath._GetChapterId();
						if(sCurrentChapterId!=oCurObj.data.sChapterId)
						{
							oCurObj.data.aPath = CL.oPath._GetSlideList({ bChapter: true });
							oCurObj.data.sChapterId = sCurrentChapterId;
							if(oCurObj.data.sDisplayType=="icons")
							{
								var jElem;
								oCurObj.data.jRail.children("[cl-id]").remove();
								for(var i=0; i<oCurObj.data.aPath.length; i++)
								{
									jElem = oCurObj.data.jTemplateU.clone(true).appendTo(oCurObj.data.jRail).show().removeClass("cl-progress-elem-template").css({ "display": "inline-block" }).attr({ "cl-id": oCurObj.data.aPath[i] });
									if(oCurObj.data.bMouseOver && !CL.bTouch)
									{
										jElem.on("mouseover", function (e) { CLO[argobj.pid].CallMethod({ sMethod: "Over", oMethodArgs: { evt: e, elem: this } }) }).on("mouseout", function (e) { CLO[argobj.pid].CallMethod({ sMethod: "Out", oMethodArgs: { evt: e, elem: this } }) });
									}
									if(oCurObj.data.bLink)
									{
										jElem.addClass("cl-linked").on("click", function (e) { CLO[argobj.pid].CallMethod({ sMethod: "Navigate", oMethodArgs: { evt: e, elem: this } }) });
									}
								}
							}
							else
							{
								jCurObjDiv.find(".cl-bar").width(Math.round(iNumber/oCurObj.data.aPath.length*100) + "%");
							}
							if(oCurObj.data.jSlideTotal!=null)
							{
								oCurObj.data.jSlideTotal.html(CL.oPath._GetLength({ bChapter: true }));
							}
						}
						iNumber = CL.oPath._GetSlideNumber({ bChapter: true });
						if(oCurObj.data.sDisplayType=="icons")
						{
							for(var i=0; i<oCurObj.data.aPath.length; i++)
							{
								jCell = $(oCurObj.div).find("[cl-id='" + oCurObj.data.aPath[i] + "']");
								if(oCurObj.data.aPath[i]==CLZ.sCurrentSlideId)
								{
									if(jCell.hasClass("cl-progress-img"))
									{
										jCell.removeClass("cl-progress-v cl-progress-u").addClass("cl-progress-c").attr({ "src": oCurObj.data.jTemplateC.attr("src") });
									}
									else
									{
										jCell.removeClass("cl-progress-v cl-progress-v-var cl-progress-u cl-progress-u-var").addClass("cl-progress-c cl-progress-c-var");
									}
									if(CLO[argobj.pid].data.bLink)
									{
										jCell.removeClass("cl-linked").unbind("click");
									}
									iCurrent = i;
								}
								else if(CL.SCO.IsVisited({ sBy: "slideid", sSlideId: oCurObj.data.aPath[i] }))
								{
									if(jCell.hasClass("cl-progress-img"))
									{
										jCell.removeClass("cl-progress-c cl-progress-u").addClass("cl-progress-v").attr({ "src": oCurObj.data.jTemplateV.attr("src") });
									}
									else
									{
										jCell.removeClass("cl-progress-c cl-progress-c-var cl-progress-u cl-progress-u-var").addClass("cl-progress-v cl-progress-v-var");
									}
									if(CLO[argobj.pid].data.bLink && !jCell.hasClass("cl-linked"))
									{
										jCell.addClass("cl-linked").click(function (e) { CLO[argobj.pid].CallMethod({ sMethod: "Navigate", oMethodArgs: { evt: e, elem: this } }) });
									}
								}
								else
								{
									if(jCell.hasClass("cl-progress-img"))
									{
										jCell.removeClass("cl-progress-c cl-progress-v").addClass("cl-progress-u").attr({ "src": oCurObj.data.jTemplateU.attr("src") });
									}
									else
									{
										jCell.removeClass("cl-progress-c cl-progress-c-var cl-progress-v cl-progress-v-var").addClass("cl-progress-u cl-progress-u-var");
									}
									if(CLO[argobj.pid].data.bLink)
									{
										if(((CLZ.bStrictOrder && i<=iCurrent+1) || !CLZ.bStrictOrder) && !jCell.hasClass("cl-linked"))
										{
											jCell.addClass("cl-linked").click(function (e) { CLO[argobj.pid].CallMethod({ sMethod: "Navigate", oMethodArgs: { evt: e, elem: this } }) });
										}
									}
								}
							}
						}
						else
						{
							jCurObjDiv.find(".cl-bar").width(Math.round(iNumber/oCurObj.data.aPath.length*100) + "%");
						}
					}
					if(oCurObj.data.jSlideNumber!=null)
					{
						oCurObj.data.jSlideNumber.html(iNumber);
					}
					if(oCurObj.data.jSlideName!=null)
					{
						oCurObj.data.jSlideName.html(CLS[CLZ.sCurrentSlideId].sName);
					}
					return true;
                }

function Navigate(argobj)
                {
					if($(CLO[argobj.pid].div).find("div.cl-container").hasClass("cl-disabled")) return false;
					var sTargetId = $(argobj.elem).attr("cl-id");
					if(argobj.override==true || CL.Navigation.Allowed({ sTarget: "slide", sTargetId: sTargetId }))
					{
						CLO[argobj.pid].CallMethod({ sMethod: "DisableAccess" });
						CL.Navigation.GoTo({ sTargetType: "slide", sTargetId: sTargetId });
					}
					CL.Sound.Stop({ sId: argobj.pid, sEvent: "click" });
					if(oCurObj.data.sSoundClick!="none")
					{
						CL.Sound.Play({ sId: argobj.pid, sEvent: "click", sType: oCurObj.data.sSoundClick, sFile: oCurObj.data.sSoundClickFile })
					}
                    return true;
                }

function Over(argobj)
				{
					var sTargetId = $(argobj.elem).attr("cl-id");
					var oCurObj = CLO[argobj.pid];
					$(CLO[argobj.pid].div).find("div.cl-over").html($(CL.jxModule).find("slide[id='" + sTargetId + "']").attr("name"));
					CL.Sound.Stop({ sId: argobj.pid, sEvent: "over" });
					if(oCurObj.data.sSoundOver!="none")
					{
						CL.Sound.Play({ sId: argobj.pid, sEvent: "over", sType: oCurObj.data.sSoundOver, sFile: oCurObj.data.sSoundOverFile })
					}
					return true;
				}

function Out(argobj)
				{
					$(CLO[argobj.pid].div).find("div.cl-over").html("&nbsp;");
					return true;
				}

function Constructor(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurDiv = $(oCurObj.div);
				oCurObj.bShowInternal = true;
				var jContainer = jCurDiv.find(".cl-container");
				jContainer.find(".cl-btn").on("click", function () { oCurObj.CallMethod({ sMethod: "Close" }) });
				oCurObj.initial.nZ = 5000;
				oCurObj.data =
				{
					aTogglers: [],
					bNaviStrict: (oCurObj.jxParams.children("nav_lock").text()=="yes"),
					bNaviLockedByAction: false,
					bModal: (oCurObj.jxParams.children("modal").text()=="yes"),
					bSearchTip: (oCurObj.jxParams.children("search_tip").text()=="yes"),
					sSlideId: "",
					sRole: oCurObj.jxParams.children("contenttype").text(),
					sState: "enabled",
					sPosition: "closed",
					sSoundClick: oCurObj.jxParams.children("sound_click").text(),
					sSoundOver: oCurObj.jxParams.children("sound_over").text()
				};
				if(oCurObj.data.sSoundClick=="custom")
				{
					oCurObj.data.sSoundClickFile = oCurObj.jxParams.children("sound_click_file_custom").text();
				}
				if(oCurObj.data.sSoundOver=="custom")
				{
					oCurObj.data.sSoundOverFile = oCurObj.jxParams.children("sound_over_file_custom").text();
				}
				var jxToggle = CL.jxModule.find("param > target_object");
				var sTogglerId;
				for(var i=0; i<jxToggle.length; i++)
				{
					if($(jxToggle[i]).text()==argobj.pid)
					{
						sTogglerId = $(jxToggle[i]).parent().attr("objectid");
						oCurObj.data.aTogglers.push(sTogglerId);
						if(oCurObj.data.sRole=="contents")
						{
							if(CLO[sTogglerId]!=null)
							{
								if(CLO[sTogglerId].data!=null)
								{
									CLO[sTogglerId].data.sRole = "contents";
								}
							}
						}
					}
				}
				var iTranIn = parseInt(oCurObj.jxParams.children("tranin").text(),10);
				if(iTranIn==-2 && oCurObj.tranin!=null)
				{
					oCurObj.data.bTranIn = true;
					oCurObj.data.iTranIn = oCurObj.tranin.transition;
					oCurObj.data.iDurIn = oCurObj.tranin.duration;
				}
				else if(iTranIn>=0)
				{
					oCurObj.data.bTranIn = true;
					oCurObj.data.iTranIn = iTranIn;
					oCurObj.data.iDurIn = parseInt(oCurObj.jxParams.children("durin").text(),10);
				}
				else
				{
					oCurObj.data.bTranIn = false;
				}
				var iTranOut = parseInt(oCurObj.jxParams.children("tranout").text(),10);
				if(iTranOut==-2 && oCurObj.tranout!=null)
				{
					oCurObj.data.bTranOut = true;
					oCurObj.data.iTranOut = oCurObj.tranout.transition;
					oCurObj.data.iDurOut = oCurObj.tranout.duration;
				}
				else if(iTranOut>=0)
				{
					oCurObj.data.bTranOut = true;
					oCurObj.data.iTranOut = iTranOut;
					oCurObj.data.iDurOut = parseInt(oCurObj.jxParams.children("durout").text(),10);
				}
				else
				{
					oCurObj.data.bTranOut = false;
				}
				oCurObj.CallMethod({ sMethod: "InitRole" });
				jCurDiv.hide();
				CL.Event.Subscribe({ aList: [ { sId: argobj.pid, sEvtName: "EVENT_OBJECT_SHOW" }, { sId: argobj.pid, sEvtName: "EVENT_SLIDE_OPENED" }, { sId: argobj.pid, sEvtName: "EVENT_SLIDE_COMPLETE" }, { sId: argobj.pid, sEvtName: "EVENT_NAVIGATION" } ] });
				return true;
			}

function HandleEvt(oEvtHandler, oArg)
			{
				if(CLO[argobj.pid]==null || CLO[argobj.pid].div==null) return false;
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				switch(argobj.evt)
				{
					case "EVENT_OBJECT_SHOW":
					{
						oCurObj.data.sPosition = "open";
						break;
					}
					case "EVENT_SLIDE_OPENED":
					{
						oCurObj.CallMethod({ sMethod: "Close" });
						if(oCurObj.data.sRole=="contents" || oCurObj.data.sRole=="settings" || oCurObj.data.sRole=="comment")
						{
							oCurObj.CallMethod({ sMethod: "Update" });
						}
						if(oCurObj.data.sSlideId!=CLZ.sCurrentSlideId)
						{
							oCurObj.data.sSlideId = CLZ.sCurrentSlideId;
							oCurObj.data.bNaviLockedByAction = false;
						}
						oCurObj.CallMethod({ sMethod: ((oCurObj.data.bNaviStrict || oCurObj.data.bNaviLockedByAction) ? "Disable" : "Enable") });
						break;
					}
					case "EVENT_SLIDE_COMPLETE":
					{
						oCurObj.CallMethod({ sMethod: ((oCurObj.data.bNaviLockedByAction) ? "Disable" : "Enable") });
						break;
					}
					case "EVENT_NAVIGATION":
					{
						if(argobj.sTargets!=null)
						{
							if(argobj.sTargets=="tabs" || argobj.sTargets=="all" || (argobj.sTargets=="contents" && oCurObj.data.sRole=="contents"))
							{
								switch(argobj.sAction)
								{
									case "off":
									{
										oCurObj.data.bNaviLockedByAction = true;
										oCurObj.CallMethod({ sMethod: "Disable" });
										break;
									}
									case "on":
									default:
									{
										oCurObj.data.bNaviLockedByAction = false;
										oCurObj.CallMethod({ sMethod: "Enable" });
										break;
									}
								}
							}
						}
						break;
					}
				}
				return true;
			}

function InitRole(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var jContainer = $(oCurObj.div).find(".cl-container");
				var iPadding = +oCurObj.jxParams.find("panel_padding").text();
				var iBorder = +oCurObj.jxParams.find("panel_borderwidth").text();
				var iFullH = oCurObj.initial.h;
				var iRadius = +oCurObj.jxParams.find("panel_radius").text();
				var iCloseH = (iRadius > iPadding) ? iRadius + 20 : iPadding + 20;
				var iHH = 0;
				var jHeader = jContainer.find(".cl-body-header");
				if(jHeader.length>0)
				{
					iHH = jHeader.outerHeight();
					if(iHH<iCloseH) iHH = iCloseH;
				}
				else
				{
					iHH = iCloseH;
				}
				iH = iFullH - iHH - 2*iPadding - 2*iBorder;
				jContainer.find(".cl-body-contents").height(iH);
				switch(oCurObj.data.sRole)
				{
					case "contents":
					{
						var jTemplate = jContainer.find("li.slide-item-template");
						var jUL = jContainer.find("ul.slide-list");
						var jxSlide;
						var jLI;
						var sSlideId;
						var bPrevVisited = true;
						var iIndent;
						var iPrevIndent = 0;
						var aPath = CL.oPath._GetSlideList();
						for(var i=0; i<aPath.length; i++)
						{
							sSlideId = aPath[i];
							jxSlide = CL.jxModule.find("slide[id='" + sSlideId + "']");
							jLI = jTemplate.clone(true).removeClass("slide-item-template").addClass("slide-item").attr({ "data-slide-id": sSlideId }).appendTo(jUL);
							jLI.on("click", function () { CLO[argobj.pid].CallMethod({ sMethod: "Navigate", oMethodArgs: { elem: this } }) });
							jLI.find(".item-inner").html(jxSlide.attr("name"));
							if(jxSlide.attr("indent")!=null)
							{
								iIndent = +jxSlide.attr("indent");
								if(iIndent!=0)
								{
									jLI.addClass("indent-" + iIndent);
									if(iIndent>iPrevIndent && CL.axSlides[i-1]!=0)
									{
										jLI.prev().addClass("open");
										iPrevIndent = iIndent;
									}
								}
								else
								{
									iPrevIndent = 0;
								}
							}
							else
							{
								iPrevIndent = 0;
							}
							if(sSlideId==CLZ.sCurrentSlideId)
							{
								jLI.addClass("current");
								bPrevVisited = true;
							}
							else if(CL.SCO.IsVisited({ sBy: "slideid", sSlideId: sSlideId }))
							{
								jLI.addClass("allowed visited");
								bPrevVisited = true;
							}
							else
							{
								if(bPrevVisited==true)
								{
									jLI.addClass("allowed unvisited");
									bPrevVisited = false;
								}
								else if(!CLZ.bStrictOrder)
								{
									jLI.addClass("allowed unvisited");
								}
								else
								{
									jLI.addClass("forbidden unvisited");
								}
							}
						}
						break;
					}
					case "search":
					{
						jContainer.find(".cl-search-results-list, .cl-search-found, .cl-search-no-results").hide();
						jContainer.find(".cl-search-btn").on("click", function () { CLO[argobj.pid].CallMethod({ sMethod: "Search" }) });
						jContainer.find(".cl-search-field").on("keydown", function () { CLO[argobj.pid].CallMethod({ sMethod: "SearchTip" }) });
						break;
					}
					case "glossary":
					{
						var jLetterBox = jContainer.find(".cl-letters-box");
						if(jLetterBox.length!=0)
						{
							var iMaxW = -1;
							var jLetters = jLetterBox.find(".cl-letter-item");
							jLetters.each(function ()
							{
								var iW = $(this).width();
								if(iW>iMaxW) iMaxW = iW;
							});
							jLetters.each(function ()
							{
								$(this).width(iMaxW);
							});
							iHH = jLetterBox.outerHeight();
							iH = iH - iHH;
						}
						jContainer.find(".cl-terms-list-container").height(iH);
						jContainer.find(".cl-terms-desc").height(iH);
						iH -= 2*iPadding;
						jContainer.find(".cl-terms-desc-container").height(iH);
						jContainer.find(".cl-terms-desc-default-table").height(iH);
						jContainer.find(".cl-terms-desc-default-cell").height(iH);
						if(jContainer.find(".cl-letter-item").length>0)
						{
							jContainer.find(".cl-letter-item").on("click", function () { oCurObj.CallMethod({ sMethod: "Filter", oMethodArgs: { elem: this } }) });
						}
						jContainer.find(".cl-term-item").on("click", function () { oCurObj.CallMethod({ sMethod: "Display", oMethodArgs: { elem: this } }) });
						break;
					}
					case "calculator":
					{
						jContainer.find(".calc-btn").on("click", function () { oCurObj.CallMethod({ sMethod: "HandleCalc", oMethodArgs: { elem: this } }) });
						oCurObj.data.sOperandA = "na";
						oCurObj.data.sOperatorA = "na";
						oCurObj.data.sOperandB = "na";
						oCurObj.data.sLastBtn = "na";
						oCurObj.data.sMemory = "na";
						oCurObj.data.bClean = false;
						oCurObj.data.bEqual = false;
						oCurObj.data.bPercent = false;
						break;
					}
					case "settings":
					{
						oCurObj.data.sObjToDisplay = oCurObj.jxParams.children("settings_display").text();
						oCurObj.data.bCustomIcons = (oCurObj.jxParams.children("settings_icons").text()=="custom");
						oCurObj.data.sScoreDisplayType = oCurObj.jxParams.children("score_style").text();
						var jTemplate = jContainer.find(".cl-sett-row-template");
						var jTable = jTemplate.parents("table:first");
						var jTR;
						var jTD;
						for(var sObjId in CLJ)
						{
							if(oCurObj.data.sObjToDisplay=="main" && CLJ[sObjId].bModule.toString()!="1") continue;
							if(oCurObj.data.sObjToDisplay=="other" && CLJ[sObjId].bModule.toString()=="1") continue;
							jTR = jTemplate.clone(true).attr({ "data-obj-id": sObjId }).removeClass("cl-sett-row-template");
							jTR.children("td.cl-sett-name").html(CLJ[sObjId].sName);
							if(oCurObj.data.sScoreDisplayType=="raw")
							{
								jTR.children("td.cl-sett-score").html(CLJ[sObjId].nRawScore);
							}
							else if(oCurObj.data.sScoreDisplayType=="rawmax")
							{
								jTR.children("td.cl-sett-score").html(CLJ[sObjId].nRawScore + "(" + CLJ[sObjId].nMax + ")");
							}
							else if(oCurObj.data.sScoreDisplayType=="scaled")
							{
								if(CLJ[sObjId].nMax!=0)
								{
									jTR.children("td.cl-sett-score").html( Math.round(100*CLJ[sObjId].nRawScore/CLJ[sObjId].nMax)/100 + "%");
								}
								else
								{
									jTR.children("td.cl-sett-score").html(CLJ[sObjId].nRawScore);
								}
							}
							if(oCurObj.data.bCustomIcons)
							{
								jTD = jTR.children("td.sett-cs");
								jTD.attr({ "title": jTD.attr("data-title-cs-" + CLJ[sObjId].sCS),"data-obj-id": sObjId });
								jTD.find("img").hide();
								jTD.find("img.sett-cs-" + CLJ[sObjId].sCS).show();
								jTD = jTR.children("td.sett-ss");
								jTD.attr({ "title": jTD.attr("data-title-ss-" + CLJ[sObjId].sSS),"data-obj-id": sObjId });
								jTD.find("img").hide();
								jTD.find("img.sett-ss-" + CLJ[sObjId].sSS).show();
							}
							else
							{
								jTD = jTR.children("td.sett-cs");
								jTD.attr({ "title": jTD.attr("data-title-cs-" + CLJ[sObjId].sCS),"data-obj-id": sObjId });
								jTD.find("div.cl-sett-status").addClass("cl-sett-status-" + CLJ[sObjId].sCS);
								jTD = jTR.children("td.sett-ss");
								jTD.attr({ "title": jTD.attr("data-title-ss-" + CLJ[sObjId].sSS),"data-obj-id": sObjId });
								jTD.find("div.cl-sett-status").addClass("cl-sett-status-" + CLJ[sObjId].sSS);
							}
							if(CLJ[sObjId].bModule.toString()=="1")
							{
								jTR.insertAfter(jTable.find("tr.sett-main-obj-header"));
							}
							else
							{
								jTR.appendTo(jTable);
							}
						}
						break;
					}
					case "comment":
					{
						oCurObj.data.sIfNoComment = oCurObj.jxParams.children("comment_display").text();
						break;
					}
				}
			}

function HandleCalc(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				if(oCurObj.data.sRole!="calculator") return false;
				if(argobj.elem==null) return false;
				var sKey = $(argobj.elem).attr("data-btn");
				if(sKey==null || sKey=="") return false;
				var jContainer = $(oCurObj.div).find(".cl-container");
				var jDisplay = jContainer.find(".calc-display");
				var jMemInd = jContainer.find(".calc-memind");
				var sCurString = $.trim(jDisplay.html() + "");
				if($(argobj.elem).hasClass("calc-num")) // numeric btn or .
				{
					sKey = sKey + "";
					if(oCurObj.data.bEqual==true)
					{
						oCurObj.data.sOperandA = "na";
						oCurObj.data.sOperatorA = "na";
						oCurObj.data.sOperandB = "na";
						oCurObj.data.bEqual = false;
					}
					if(oCurObj.data.bClean==true)
					{
						sCurString = "0";
						jDisplay.html("0");
						oCurObj.data.sOperandB = "na";
						oCurObj.data.bClean = false;
					}
					if(sCurString=="0" && sKey=="0") return false;
					if(sKey=="." && sCurString.indexOf(".")!=-1) return false;
					if(sCurString.length>=17)
					{
						if(sCurString.indexOf("!")==-1) jDisplay.html("!"+sCurString);
						return false;
					}
					if(sCurString.length==1 && sCurString=="0" && sKey!=".")
					{
						jDisplay.html(sKey);
						return true;
					}
					jDisplay.html(sCurString + sKey);
				}
				else
				{
					if(sCurString.indexOf("ERROR")!=-1 && sKey!="C") return false;
					switch(sKey)
					{
						case "M+":
						{
							if(sCurString.indexOf("!")!=-1) sCurString = sCurString.substr(1);
							jMemInd.html("M");
							if(oCurObj.data.sMemory=="na")
							{
								oCurObj.data.sMemory = sCurString;
							}
							else
							{
								var nM1 = parseFloat(oCurObj.data.sMemory);
								var nM2 = parseFloat(sCurString);
								nM1 = nM1 + nM2;
								oCurObj.data.sMemory = nM1.toString();
							}
							break;
						}
						case "MS":
						{
							if(sCurString.indexOf("!")!=-1) sCurString = sCurString.substr(1);
							jMemInd.html("M");
							if(oCurObj.data.sMemory=="na")
							{
								oCurObj.data.sMemory = "-" + sCurString;
							}
							else
							{
								var nM1 = parseFloat(oCurObj.data.sMemory);
								var nM2 = parseFloat(sCurString);
								nM1 = nM1 - nM2;
								oCurObj.data.sMemory = nM1.toString();
							}
							break;
						}
						case "MR":
						{
							if(oCurObj.data.sMemory!="na")
							{
								jDisplay.html(oCurObj.data.sMemory);
							}
							break;
						}
						case "MC":
						{
							oCurObj.data.sMemory = "na";
							jMemInd.html("&nbsp;");
							break;
						}
						case "C":
						{
							oCurObj.data.sOperandA = "na";
							oCurObj.data.sOperatorA = "na";
							oCurObj.data.sOperandB = "na";
							oCurObj.data.sLastBtn = "na";
							oCurObj.data.bClean = false;
							jDisplay.html("0");
							break;
						}
						case "CE":
						{
							oCurObj.data.sOperandB = "na";
							oCurObj.data.sLastBtn = "na";
							oCurObj.data.bClean = false;
							jDisplay.html("0");
							break;
						}
						case "Backspace":
						{
							if(sCurString.length>1)
							{
								sCurString = sCurString.substr(0,sCurString.length-1);
							}
							jDisplay.html(sCurString);
							break;
						}
						case "equal":
						{
							if(oCurObj.data.bPercent==true)
							{
								return false;
							}
							oCurObj.data.bEqual = true;
							if(oCurObj.data.sOperandA!="na")
							{
								oCurObj.CallMethod({ sMethod: "Calculate", oMethodArgs: { sKey: sKey } });
							}
							break;
						}
						case "power":
						case "plus":
						case "minus":
						case "mult":
						case "divide":
						{
							if(oCurObj.data.bEqual==true)
							{
								oCurObj.data.bEqual = false;
							}
							if(oCurObj.data.bPercent==true)
							{
								oCurObj.data.bPercent = false;
							}
							if(sCurString.indexOf("!")!=-1)
							{
								sCurString = sCurString.substr(1);
								jDisplay.html(sCurString);
							}
							if(oCurObj.data.sOperandA=="na" || oCurObj.data.bClean==true)
							{
								oCurObj.data.sOperandA = sCurString;
								oCurObj.data.sOperatorA = sKey;
								oCurObj.data.bClean = true;
							}
							else
							{
								oCurObj.CallMethod({ sMethod: "Calculate", oMethodArgs: { sKey: sKey } });
							}
							break;
						}
						case "negate":
						{
							if(sCurString=="0") return false;
							if(oCurObj.data.bEqual==true)
							{
								oCurObj.data.bEqual = false;
							}
							if(oCurObj.data.bPercent==true)
							{
								oCurObj.data.bPercent = false;
							}
							if(sCurString.indexOf("-")!=-1)
							{
								sCurString = sCurString.substr(1);
							}
							else
							{
								sCurString = "-"+sCurString;
							}
							jDisplay.html(sCurString);
							break;
						}
						case "recip":
						{
							if(sCurString=="0")
							{
								oCurObj.data.sOperandA = "na";
								oCurObj.data.sOperatorA = "na";
								oCurObj.data.sOperandB = "na";
								oCurObj.data.sLastBtn = "na";
								oCurObj.data.bClean = false;
								jDisplay.html("ERROR: DIV 0");
								return false;
							}
							if(oCurObj.data.bEqual==true)
							{
								oCurObj.data.bEqual = false;
							}
							if(oCurObj.data.bPercent==true)
							{
								oCurObj.data.bPercent = false;
							}
							if(sCurString.indexOf("!")!=-1) sCurString = sCurString.substr(1);
							var nValue = 1/parseFloat(sCurString);
							sCurString = nValue.toString();
							if(sCurString.length>=20)
							{
								sCurString = sCurString.substring(0,19);
							}
							jDisplay.html(sCurString);
							break;
						}
						case "percent":
						{
							if(oCurObj.data.sOperandA=="na") return false;
							if(oCurObj.data.bPercent==true) return false;
							oCurObj.data.bPercent = true;
							if(oCurObj.data.bEqual==true)
							{
								oCurObj.data.bEqual = false;
							}
							oCurObj.CallMethod({ sMethod: "Calculate", oMethodArgs: { sKey: sKey } });
							oCurObj.data.sOperandB = "na";
							break;
						}
					}

				}
				return true;
			}

function Calculate(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				if(oCurObj.data.sRole!="calculator") return false;
				var jContainer = $(oCurObj.div).find(".cl-container");
				var jDisplay = jContainer.find(".calc-display");
				var jMemInd = jContainer.find(".calc-memind");
				var sCurString = $.trim(jDisplay.html() + "");
				if(sCurString.indexOf("!")!=-1) sCurString = sCurString.substr(1);
				var nValue1 = parseFloat(oCurObj.data.sOperandA);
				if(oCurObj.data.sOperandB=="na")
				{
					oCurObj.data.sOperandB = sCurString
				}
				var nValue2 = parseFloat(oCurObj.data.sOperandB);
				var nResult = 0;
				switch(oCurObj.data.sOperatorA)
				{
					case "plus":
					{
						nResult = (nValue1*1000 + nValue2*1000)/1000;
						break;
					}
					case "minus":
					{
						nResult = (nValue1*1000 - nValue2*1000)/1000;
						break;
					}
					case "mult":
					{
						nResult = nValue1 * nValue2;
						if(oCurObj.data.bPercent==true)
						{
							nResult = nResult/100;
						}
						break;
					}
					case "divide":
					{
						if(nValue2==0)
						{
							oCurObj.data.sOperandA = "na";
							oCurObj.data.sOperatorA = "na";
							oCurObj.data.sOperandB = "na";
							oCurObj.data.sLastBtn = "na";
							oCurObj.data.bClean = true;
							jDisplay.html("ERROR: DIV 0");
							return false;
						}
						nResult = nValue1 / nValue2;
						if(oCurObj.data.bPercent==true)
						{
							nResult = nResult*100;
						}
						break;
					}
					case "power":
					{
						nResult = Math.pow(nValue1,nValue2);
						break;
					}
				}

				if(nResult>-1 && nResult<1)
				{
					nResult = nResult.toString();
					if(nResult.length>=20)
					{
						if(nResult.indexOf("e")!=-1)
						{
							var aParts = vResult.split("e");
							if(parseInt(aParts[1])<-20)
							{
								nResult = "0";
							}
							else
							{
								if(aParts[0].length>14)
								{
									aParts[0] = aParts[0].substr(0,13);
									if(parseFloat(aParts[0])==0)
									{
										nResult = "0";
									}
									else
									{
										nResult = aParts.join("e");
									}
								}
								else
								{
									nResult = aParts.join("e");
								}
							}
						}
						else
						{
							nResult = nResult.substr(0,19);
						}
					}
				}
				else
				{
					nResult = nResult.toString();
					if(nResult.length>=18)
					{
						if(nResult.indexOf(".")==-1)
						{
							oCurObj.data.sOperandA = "na";
							oCurObj.data.sOperatorA = "na";
							oCurObj.data.sOperandB = "na";
							oCurObj.data.sLastBtn = "na";
							oCurObj.data.bClean = true;
							jDisplay.html("ERROR: OVERFLOW");
							return false;
						}
						else
						{
							if(nResult.indexOf("e")!=-1)
							{
								var aParts = nResult.split("e");
								if(aParts[0].length>14)
								{
									aParts[0] = aParts[0].substr(0,13);
									nResult = aParts.join("e")
								}
							}
							else
							{
								nResult = nResult.substr(0,17);
							}
						}
					}
				}
				jDisplay.html(nResult);
				oCurObj.data.sOperandA = nResult;
				if(oCurObj.data.bEqual==false && oCurObj.data.bPercent==false)
				{
					oCurObj.data.sOperatorA = argobj.sKey;
				}
				oCurObj.data.bClean = true;
				return true;
			}

function Filter(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				if(oCurObj.data.sRole!="glossary") return false;
				if(argobj.elem==null) return false;
				if($(argobj.elem).hasClass("cl-letter-selected")) return false;
				var jContainer = $(oCurObj.div).find(".cl-container");
				var jTerms = jContainer.find(".cl-term-item");
				var sLetter = $.trim($(argobj.elem).text());
				if(sLetter=="*")
				{
					jTerms.show();
				}
				else
				{
					jTerms.hide().each(function ()
					{
						var sText = $.trim($(this).html());
						if(sText.toUpperCase().charAt(0)==sLetter)
						{
							$(this).show();
						}
					});
				}
				jTerms.filter(":visible").filter(":first").click();
				jContainer.find(".cl-letter-item").addClass("cl-letter-idle").removeClass("cl-letter-selected");
				$(argobj.elem).removeClass("cl-letter-idle").addClass("cl-letter-selected");
			}

function Display(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				if(oCurObj.data.sRole!="glossary") return false;
				var jContainer = $(oCurObj.div).find(".cl-container");
				var jTerms = jContainer.find(".cl-term-item");
				if(argobj.ecode!=null || argobj.eitem!=null)
				{
					var sCode = $.trim(argobj.ecode);
					var sText = $.trim(argobj.eitem);
					if(sCode=="" && sText=="") return false;
					var jItem;
					if(sCode!="")
					{
						jItem = jTerms.filter("[data-code='" + sCode + "']");
						if(jItem.length!=0)
						{
							if(CLO[argobj.pid].data.sPosition=="closed" && CLO[argobj.pid].data.sState=="enabled")
							{
								CLO[argobj.pid].CallMethod({ sMethod: "Toggle" });
							}
							if(!jItem.hasClass("cl-term-selected"))
							{
								jItem.click();
							}
						}
					}
					else if(sText!="")
					{
						var sItemText;
						for(var i=0; i<jTerms.length; i++)
						{
							sItemText = $.trim($(jTerms[i]).text());
							if(sText==sItemText)
							{
								jItem = $(jTerms[i]);
								break;
							}
						}
						if(jItem.length!=0)
						{
							if(CLO[argobj.pid].data.sPosition=="closed" && CLO[argobj.pid].data.sState=="enabled")
							{
								CLO[argobj.pid].CallMethod({ sMethod: "Toggle" });
							}
							if(!jItem.hasClass("cl-term-selected"))
							{
								jItem.click();
							}
						}
					}
				}
				else
				{
					if(argobj.elem==null) return false;
					if($(argobj.elem).hasClass("cl-letter-selected")) return false;
					var sTermId = $(argobj.elem).attr("data-number");
					jTerms.removeClass("cl-term-selected").addClass("cl-term-idle");
					jContainer.find(".cl-term-desc-item").hide();
					jContainer.find(".cl-term-desc-item[data-number='" + sTermId + "']").show("fade");
					jContainer.find(".cl-term-item[data-number='" + sTermId + "']").removeClass("cl-term-idle").addClass("cl-term-selected");
				}
				return true;
			}

function Update(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var jContainer = $(oCurObj.div).find(".cl-container");
				switch(oCurObj.data.sRole)
				{
					case "contents":
					{
						var jLIs = jContainer.find("ul.slide-list > li.slide-item");
						var sSlideId;
						var jLI;
						var bPrevVisited = true;
						for(var i=0; i<jLIs.length; i++)
						{
							jLI = $(jLIs[i]);
							sSlideId = jLI.attr("data-slide-id");
							if(sSlideId==CLZ.sCurrentSlideId)
							{
								jLI.removeClass("allowed forbidden visited unvisited").addClass("current");
								bPrevVisited = true;
							}
							else if(CL.SCO.IsVisited({ sBy: "slideid", sSlideId: sSlideId }))
							{
								jLI.removeClass("current unvisited forbidden").addClass("allowed visited");
								bPrevVisited = true;
							}
							else
							{
								if(bPrevVisited)
								{
									if(CL.oPath._TargetAllowed({ sSlideId: sSlideId }))
									{
										jLI.removeClass("current unvisited forbidden").addClass("allowed unvisited");
									}
									else
									{
										jLI.removeClass("allowed current").addClass("forbidden unvisited");
									}
									bPrevVisited = false;
								}
								else
								{
									if(!CLZ.bStrictOrder)
									{
										if(CL.oPath._TargetAllowed({ sSlideId: sSlideId }))
								{
											jLI.removeClass("current unvisited forbidden").addClass("allowed unvisited");
								}
								else
								{
											jLI.removeClass("allowed current").addClass("forbidden unvisited");
										}
									}
									else
									{
										jLI.removeClass("allowed current").addClass("forbidden unvisited");
									}
								}
							}
						}
						break;
					}
					case "settings":
					{
						var jTR;
						for(var sObjId in CLJ)
						{
							if(oCurObj.data.sObjToDisplay=="main" && CLJ[sObjId].bModule.toString()!="1") continue;
							if(oCurObj.data.sObjToDisplay=="other" && CLJ[sObjId].bModule.toString()=="1") continue;
							jTR = jContainer.find("tr[data-obj-id='" + sObjId + "']");
							if(oCurObj.data.sScoreDisplayType=="raw")
							{
								jTR.children("td.cl-sett-score").html(CLJ[sObjId].nRawScore);
							}
							else if(oCurObj.data.sScoreDisplayType=="rawmax")
							{
								jTR.children("td.cl-sett-score").html(CLJ[sObjId].nRawScore + "(" + CLJ[sObjId].nMax + ")");
							}
							else if(oCurObj.data.sScoreDisplayType=="scaled")
							{
								if(CLJ[sObjId].nMax!=0)
								{
									jTR.children("td.cl-sett-score").html( Math.round(100*CLJ[sObjId].nRawScore/CLJ[sObjId].nMax)/100 + "%");
								}
								else
								{
									jTR.children("td.cl-sett-score").html(CLJ[sObjId].nRawScore);
								}
							}
							if(oCurObj.data.bCustomIcons)
							{
								jTD = jTR.children("td.sett-cs");
								jTD.attr({ "title": jTD.attr("data-title-cs-" + CLJ[sObjId].sCS) });
								jTD.find("img").hide();
								jTD.find("img.sett-cs-" + CLJ[sObjId].sCS).show();
								jTD = jTR.children("td.sett-ss");
								jTD.attr({ "title": jTD.attr("data-title-ss-" + CLJ[sObjId].sSS) });
								jTD.find("img").hide();
								jTD.find("img.sett-ss-" + CLJ[sObjId].sSS).show();
							}
							else
							{
								jTD = jTR.children("td.sett-cs");
								jTD.attr({ "title": jTD.attr("data-title-cs-" + CLJ[sObjId].sCS) });
								jTD.find("div.cl-sett-status").removeClass("cl-sett-status-n cl-sett-status-i cl-sett-status-c").addClass("cl-sett-status-" + CLJ[sObjId].sCS);
								jTD = jTR.children("td.sett-ss");
								jTD.attr({ "title": jTD.attr("data-title-ss-" + CLJ[sObjId].sSS) });
								jTD.find("div.cl-sett-status").removeClass("cl-sett-status-u cl-sett-status-p cl-sett-status-f").addClass("cl-sett-status-" + CLJ[sObjId].sSS);
							}
						}
						break;
					}
					case "comment":
					{
						var jxComments = CLS[CLZ.sCurrentSlideId].jxNode.children("comments");
						if(jxComments.length!=0)
						{
							if(jxComments.text()!="")
							{
								var sText = $("<div/>").html(jxComments.text()).text();
								if(oCurObj.data.sIfNoComment=="disable")
								{
									oCurObj.data.bNaviLockedByAction = false;
									oCurObj.CallMethod({ sMethod: "Enable" });
								}
								else if(oCurObj.data.sIfNoComment=="hide")
								{
									oCurObj.Show();
								}
								jContainer.find(".cl-comm-none").hide();
								jContainer.find(".cl-comm-body").html(sText).show();
							}
							else
							{
								if(oCurObj.data.sIfNoComment=="text")
								{
									jContainer.find(".cl-comm-none").show();
									jContainer.find(".cl-comm-body").hide();
								}
								else if(oCurObj.data.sIfNoComment=="disable")
								{
									oCurObj.data.bNaviLockedByAction = true;
									oCurObj.CallMethod({ sMethod: "Disable" });
								}
								else
								{
									oCurObj.Hide();
								}
							}
						}
						else
						{
							if(oCurObj.data.sIfNoComment=="text")
							{
								jContainer.find(".cl-comm-none").show();
								jContainer.find(".cl-comm-body").hide();
							}
							else if(oCurObj.data.sIfNoComment=="disable")
							{
								oCurObj.data.bNaviLockedByAction = true;
								oCurObj.CallMethod({ sMethod: "Disable" });
							}
							else
							{
								oCurObj.Hide();
							}
						}
						break;
					}
				}
				return true;
			}

function SearchTip(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				if(!oCurObj.data.bSearchTip)
				{
					return false;
				}
				var jContainer = $(oCurObj.div).find(".cl-container");
				var jTips = jContainer.find(".cl-search-tips");
				if(!jTips.is(":visible"))
				{
					jContainer.find(".cl-search-results-list, .cl-search-found, .cl-search-no-results").hide();
					jTips.fadeIn("fast");
				}
				return true;
			}

function Search(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var jContainer = $(oCurObj.div).find(".cl-container");
				var sStr = jContainer.find("input.cl-search-field").val();
				sStr = $.trim(sStr);
				if(sStr=="") return false;
				var sCase = oCurObj.jxParams.children("search_case").text();
				var bCase = false;
				if(sCase=="no_user" || sCase=="yes_user")
				{
					bCase = (jContainer.find("select.cl-search-case").val()=="yes");
				}
				else
				{
					bCase = (sCase=="yes");
				}
				var sLogic = oCurObj.jxParams.children("search_logic").text();
				if(sLogic=="OR_user" || sLogic=="AND_user")
				{
					sLogic = (jContainer.find("select.cl-search-logic").val()=="yes") ? "AND" : "OR";
				}
				var sScope = oCurObj.jxParams.children("search_scope").text();
				var aResults = CL.Search({ sString: sStr, bCase: bCase, sLogic: sLogic, sScope: sScope });
				if(oCurObj.data.bSearchTip)
				{
					jContainer.find(".cl-search-tips").hide();
				}
				if(aResults.length==0)
				{
					jContainer.find(".cl-search-results-list, .cl-search-found").hide();
					jContainer.find(".cl-search-no-results").show();
				}
				else
				{
					var jUL = jContainer.find(".cl-search-results-list");
					jUL.html("").show();
					jContainer.find(".cl-search-found").show();
					jContainer.find(".cl-search-found").find(".cl-search-found-qty").html(aResults.length);
					jContainer.find(".cl-search-no-results").hide();
					var jLI;
					var jTemplate = jContainer.find("li.cl-search-item-template");
					var sReturnFormat = oCurObj.jxParams.children("search_return_format").text();
					var sSlideId;
					for(var i=0; i<aResults.length; i++)
					{
						sSlideId = $(aResults[i].slide).attr("id");
						jLI = jTemplate.clone(true).removeClass("cl-search-item-template").addClass("cl-search-results-item").attr({ "data-slide-id": sSlideId }).appendTo(jUL);
						jLI.find(".item-name").html($(aResults[i].slide).attr("name"));
						if(CL.SCO.IsVisited({ sBy: "slideid", sSlideId: sSlideId }) || sSlideId==CLZ.sCurrentSlideId)
						{
							jLI.addClass("allowed visited").on("click", function () { CLO[argobj.pid].CallMethod({ sMethod: "Navigate", oMethodArgs: { elem: this } }) });
						}
						if(sReturnFormat=="extended")
						{
							var aStrings = aResults[i].strings;
							var sString = "";
							for(var j=0; j<aStrings.length; j++)
							{
								if(j==0)
								{
									sString += aStrings[j];
								}
								else
								{
									sString += "<br>" + aStrings[j];
								}
							}
							jLI.find(".item-text").html(sString);
						}
						else
						{
							jLI.find(".item-text").hide();
						}
					}
				}
				return true;
			}

function Navigate(argobj)
			{
				if(argobj.elem==null) return false;
				if($(argobj.elem).hasClass("allowed"))
				{
				var oCurObj = CLO[argobj.pid];
				var sTargetId = $(argobj.elem).attr("data-slide-id");
				CL.Navigation.GoTo({ sTargetType: "slide", sTargetId: sTargetId });
				CL.Sound.Stop({ sId: argobj.pid, sEvent: "click" });
				if(oCurObj.data.sSoundClick!="none")
				{
					CL.Sound.Play({ sId: argobj.pid, sEvent: "click", sType: oCurObj.data.sSoundClick, sFile: oCurObj.data.sSoundClickFile })
				}
				}
				return true;
			}

function Open(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				if(oCurObj.data.sPosition=="closed" && oCurObj.data.sState=="enabled")
				{
					oCurObj.CallMethod({ sMethod: "Toggle" });
				}
				return true;
			}

function Close(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				if(oCurObj.data.sPosition=="open" && oCurObj.data.sState=="enabled")
				{
					oCurObj.CallMethod({ sMethod: "Toggle" });
				}
				return true;
			}

function Disable(argobj)
			{
				if(CLO[argobj.pid].data.sPosition=="open")
				{
					CLO[argobj.pid].CallMethod({ sMethod: "Toggle" });
				}
				CLO[argobj.pid].data.sState=="disabled";
				return true;
			}

function Enable(argobj)
			{
				CLO[argobj.pid].data.sState=="enabled";
				return true;
			}

function GetProperty(argobj)
			{
				if(argobj.pid==null || argobj.property==null) return false;
				switch(argobj.property.toLowerCase())
				{
					case "disabled":
					{
						return (CLO[argobj.pid].data.sState=="disabled") ? "1" : "0";
					}
					case "open":
					{
						return (CLO[argobj.pid].data.sPosition=="open") ? "1" : "0";
					}
				}
				return "";
			}

function Toggle(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurDiv = $(oCurObj.div);
				if(oCurObj.data.sPosition=="closed")
				{
					if(oCurObj.data.bModal)
					{
						CL.Modal({ bOn: true, sSrcId: argobj.pid });
					}
					oCurObj.data.sPosition = "transition_open";
					jCurDiv.css({ "z-index": 5001 });
					if(oCurObj.data.bTranIn)
					{
						oCurObj.Show({ bActionCall: true, bUseTransition: true, iTransition: oCurObj.data.iTranIn, iDuration: oCurObj.data.iDurIn, fnCallback: function () { CLO[argobj.pid].data.sPosition = "open"; } });
					}
					else
					{
						oCurObj.Show({ bActionCall: true, bUseTransition: false });
						oCurObj.data.sPosition = "open";
					}
					if(oCurObj.data.aTogglers.length>0)
					{
						for(var i=0; i<oCurObj.data.aTogglers.length; i++)
						{
							if(CLO[oCurObj.data.aTogglers[i]].oMethods["Off"]!=null)
							{
								CLO[oCurObj.data.aTogglers[i]].CallMethod({ sMethod: "Off" });
							}
						}
					}
				}
				else
				{
					if(oCurObj.data.bModal)
					{
						CL.Modal({ bOn: false, sSrcId: argobj.pid });
					}
					oCurObj.data.sPosition = "transition_close";
					if(oCurObj.data.bTranOut)
					{
						oCurObj.Hide({ bActionCall: true, bUseTransition: true, iTransition: oCurObj.data.iTranOut, iDuration: oCurObj.data.iDurOut, fnCallback: function () { CLO[argobj.pid].data.sPosition = "closed"; } });
					}
					else
					{
						oCurObj.Hide({ bActionCall: true, bUseTransition: false });
						oCurObj.data.sPosition = "closed";
					}
					if(oCurObj.data.aTogglers.length>0)
					{
						for(var i=0; i<oCurObj.data.aTogglers.length; i++)
						{
							if(CLO[oCurObj.data.aTogglers[i]].oMethods["On"]!=null)
							{
								CLO[oCurObj.data.aTogglers[i]].CallMethod({ sMethod: "On" });
							}
						}
					}
				}
				CL.Sound.Stop({ sId: argobj.pid, sEvent: "click" });
				if(oCurObj.data.sSoundClick!="none")
				{
					CL.Sound.Play({ sId: argobj.pid, sEvent: "click", sType: oCurObj.data.sSoundClick, sFile: oCurObj.data.sSoundClickFile })
				}
				return true;
			}

function Constructor(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv= $(oCurObj.div);
				var sRole = oCurObj.jxParams.children("role").text();
				var sTarget = (sRole.indexOf("slide")==-1) ? "frame" : "slide";
				var sDir = "";
				var sId;
				if(sRole=="closemodule")
				{
					oCurObj.data =
					{
						sRole: "close",
						sState: "enabled",
						sSoundClick: oCurObj.jxParams.children("sound_click").text(),
						sSoundOver: oCurObj.jxParams.children("sound_over").text()
					};
					if(oCurObj.data.sSoundClick=="custom")
					{
						oCurObj.data.sSoundClickFile = oCurObj.jxParams.children("sound_click_file_custom").text();
					}
					if(oCurObj.data.sSoundOver=="custom")
					{
						oCurObj.data.sSoundOverFile = oCurObj.jxParams.children("sound_over_file_custom").text();
					}
				}
				else
				{
					var bSwipe = false;
					if(sRole.indexOf("next")!=-1)
					{
						sDir = "next";
						bSwipe = (oCurObj.jxParams.children("swipe").text()=="yes");
					}
					else if(sRole.indexOf("prev")!=-1)
					{
						sDir = "prev";
						bSwipe = (oCurObj.jxParams.children("swipe").text()=="yes");
					}
					else if(sRole.indexOf("first")!=-1)
					{
						sDir = "first";
					}
					else if(sRole.indexOf("last")!=-1)
					{
						sDir = "last";
					}
					else if(sRole=="thisslide")
					{
						sDir = "this";
					}
					else
					{
						sId = oCurObj.jxParams.children((sRole=="selectedslide") ? "selectedslide" : "selectedframe").text();
					}
					oCurObj.data =
					{
						sRole: sRole,
						sTarget: sTarget,
						sDir: sDir,
						sId: sId,
						sFrameId: "",
						sSlideId: "",
						sState: "enabled",
						bNaviStrict: (oCurObj.jxParams.children("nav_lock").text()=="yes"),
						bNaviLockedByAction: false,
						sSoundClick: oCurObj.jxParams.children("sound_click").text(),
						sSoundOver: oCurObj.jxParams.children("sound_over").text()
					};
					jCurObjDiv.addClass("cl-nav-action-" + oCurObj.data.sRole);
					if(oCurObj.data.sSoundClick=="custom")
					{
						oCurObj.data.sSoundClickFile = oCurObj.jxParams.children("sound_click_file_custom").text();
					}
					if(oCurObj.data.sSoundOver=="custom")
					{
						oCurObj.data.sSoundOverFile = oCurObj.jxParams.children("sound_over_file_custom").text();
					}
					if(oCurObj.data.sRole=="close")
					{
						oCurObj.data.bDisplayConfirm = (oCurObj.jxParams.children("display_closemodule_msg").text()=="yes");
					}
					CL.Event.Subscribe({ aList: [ { sId: argobj.pid, sEvtName: "EVENT_FRAME_OPENED" }, { sId: argobj.pid, sEvtName: "EVENT_SLIDE_OPENED" }, { sId: argobj.pid, sEvtName: "EVENT_FRAME_COMPLETE" }, { sId: argobj.pid, sEvtName: "EVENT_SLIDE_COMPLETE" }, { sId: argobj.pid, sEvtName: "EVENT_NAVIGATION" } ] });
				}
				if(CL.bTouch)
				{
					if(bSwipe)
					{
						if(sDir=="next")
						{
							$(CL.oBoard).on("swipeleft", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); e.originalEvent.stopPropagation(); return false; });
						}
						else if(sDir=="prev")
						{
							$(CL.oBoard).on("swiperight", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); e.originalEvent.stopPropagation(); return false; });
						}

					}
					jCurObjDiv.find(".cl-btn").addClass("cl-idle").on("click", function () { oCurObj.CallMethod({ sMethod: "Click", oMethodArgs: { pid: argobj.pid } }) });
				}
				else
				{
					jCurObjDiv.find(".cl-btn").addClass("cl-idle").on("mouseover", function () { oCurObj.CallMethod({ sMethod: "SetState", oMethodArgs: { pid: argobj.pid, sState: "over" } }) }).on("mouseout", function () { oCurObj.CallMethod({ sMethod: "SetState", oMethodArgs: { pid: argobj.pid, sState: "out" } }) }).on("click", function () { CLO[argobj.pid].CallMethod({ sMethod: "Click", oMethodArgs: { pid: argobj.pid } }) });
				}
				return true;
			}

function HandleEvt(argobj)
			{
				if(CLO[argobj.pid]==null || CLO[argobj.pid].div==null) return false;
				var oCurObj = CLO[argobj.pid];
				if(CLO[argobj.pid].data.sTarget=="slide")
				{
					switch(argobj.evt)
					{
						case "EVENT_SLIDE_OPENED":
						{
							if(oCurObj.data.sSlideId!=CLZ.sCurrentSlideId)
							{
								oCurObj.data.sSlideId = CLZ.sCurrentSlideId;
								oCurObj.data.bNaviLockedByAction = false;
							}
							oCurObj.CallMethod({ sMethod: ((oCurObj.data.bNaviStrict || oCurObj.data.bNaviLockedByAction) ? "DisableAccess" : "EnableAccess") });
							break;
						}
						case "EVENT_SLIDE_COMPLETE":
						{
							oCurObj.CallMethod({ sMethod: ((oCurObj.data.bNaviLockedByAction) ? "DisableAccess" : "EnableAccess") });
							break;
						}
						case "EVENT_NAVIGATION":
						{
							if(argobj.sTargets!=null)
							{
								if(argobj.sTargets==oCurObj.data.sDir || (argobj.sTargets=="restart" && oCurObj.data.sDir=="this") || argobj.sTargets=="all" || (argobj.sTargets=="buttons" && (oCurObj.data.sDir=="next" || oCurObj.data.sDir=="prev")) || argobj.sTargets=="allbuttons")
								{
									switch(argobj.sAction)
									{
										case "off":
										{
											oCurObj.data.bNaviLockedByAction = true;
											oCurObj.CallMethod({ sMethod: "DisableAccess" });
											break;
										}
										case "update":
										{
											oCurObj.CallMethod({ sMethod: "Update" });
											break;
										}
										case "on":
										default:
										{
											oCurObj.data.bNaviLockedByAction = false;
											oCurObj.CallMethod({ sMethod: "EnableAccess" });
											break;
										}
									}
								}
							}
							break;
						}
					}
				}
				else
				{
					switch(argobj.evt)
					{
						case "EVENT_FRAME_OPENED":
						{
							if(oCurObj.data.sFrameId!=CLZ.sCurrentFrameId)
							{
								oCurObj.data.sFrameId = CLZ.sCurrentFrameId;
								oCurObj.data.bNaviLockedByAction = false;
							}
							oCurObj.CallMethod({ sMethod: ((oCurObj.data.bNaviStrict || oCurObj.data.bNaviLockedByAction) ? "DisableAccess" : "EnableAccess") });
							break;
						}
						case "EVENT_FRAME_COMPLETE":
						{
							oCurObj.CallMethod({ sMethod: ((oCurObj.data.bNaviLockedByAction) ? "DisableAccess" : "EnableAccess") });
							break;
						}
						case "EVENT_NAVIGATION":
						{
							if(argobj.sTargets!=null)
							{
								if(argobj.sTargets=="all" || argobj.sTargets=="buttons" || argobj.sTargets=="allbuttons")
								{
									switch(argobj.sAction)
									{
										case "off":
										{
											oCurObj.data.bNaviLockedByAction = true;
											oCurObj.CallMethod({ sMethod: "DisableAccess" });
											break;
										}
										case "on":
										default:
										{
											oCurObj.data.bNaviLockedByAction = false;
											oCurObj.CallMethod({ sMethod: "EnableAccess" });
											break;
										}
									}
								}
							}
							break;
						}
					}
				}
				return true;
			}

function UIEvent(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv= $(oCurObj.div);
				if(argobj.evt==null) return false;
				if(oCurObj.data.sState=="disabled" || $(".cl-modal-mask").is(":visible")) return false;
				switch(argobj.evt.type)
				{
					case "swiperight":
					case "swipeleft":
					{
						oCurObj.CallMethod({ sMethod: "Navigate" });
						break;
					}
				}
				return true;
			}

function SetState(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jBtn = $(CLO[argobj.pid].div).find(".cl-btn");
				if(jBtn.hasClass("cl-disabled") && argobj.sState!="enabled") return false;
				switch(argobj.sState)
				{
					case "over":
					{
						jBtn.removeClass("cl-idle").addClass("cl-over");
						CL.Sound.Stop({ sId: argobj.pid, sEvent: "over" });
						if(oCurObj.data.sSoundOver!="none")
						{
							CL.Sound.Play({ sId: argobj.pid, sEvent: "over", sType: oCurObj.data.sSoundOver, sFile: oCurObj.data.sSoundOverFile })
						}
						break;
					}
					case "out":
					{
						jBtn.removeClass("cl-over").addClass("cl-idle");
						break;
					}
					case "disabled":
					{
						jBtn.removeClass("cl-idle cl-over").addClass("cl-disabled");
						CLO[argobj.pid].data.sState = "disabled";
						break;
					}
					case "enabled":
					{
						jBtn.removeClass("cl-disabled").addClass("cl-idle");
						CLO[argobj.pid].data.sState = "enabled";
						break;
					}
				}
				return true;
			}

function DisableAccess(argobj)
			{
				CLO[argobj.pid].CallMethod({ sMethod: "SetState", oMethodArgs: { sState: "disabled" } });
				return true;
			}

function EnableAccess(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				if(CLO[argobj.pid].data.sRole=="close")
				{
					CLO[argobj.pid].CallMethod({ sMethod: "SetState", oMethodArgs: { sState: "enabled" } });
				}
				else
				{
					var oParams = { sTarget: CLO[argobj.pid].data.sTarget };
					if(CLO[argobj.pid].data.sDir=="")
					{
						oParams.sTargetId = CLO[argobj.pid].data.sId;
					}
					else
					{
						oParams.sDir = CLO[argobj.pid].data.sDir;
					}
					CLO[argobj.pid].CallMethod({ sMethod: "SetState", oMethodArgs: { sState: ( (argobj.override==true || CL.Navigation.Allowed(oParams) || (CL.bAlternativePath && CL.Navigation.Exists(oParams) )) ? "enabled" : "disabled") } });
				}
				return true;
			}

function Navigate(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var oParams = { sTargetType: oCurObj.data.sTarget, bUserAction: true };
				if(oCurObj.data.sDir=="")
				{
					oParams.sTargetId = oCurObj.data.sId;
				}
				else
				{
					oParams.sTargetDir = oCurObj.data.sDir;
				}
				var sCLXNavi = "";
				CLS[CLZ.sCurrentSlideId].jxObjects.each(function()
				{
					if(this.getAttribute("type")=="clx_navigation")
					{
						sCLXNavi = this.getAttribute("id");
					}
				});
				if(sCLXNavi!="")
				{
					if((oCurObj.data.sDir=="next" && CLO[sCLXNavi].data.bCustomNext) || (oCurObj.data.sDir=="prev" && CLO[sCLXNavi].data.bCustomPrev))
					{
						return CLO[sCLXNavi].CallMethod({ sMethod: "ProcessNavigation", oMethodArgs: { sDir: oCurObj.data.sDir } });
					}
				}
				if(argobj.override==true || CL.Navigation.Allowed(oParams))
				{
					oCurObj.CallMethod({ sMethod: "SetState", oMethodArgs: { sState: "disabled" } });
					CL.Navigation.GoTo(oParams);
				}
				CL.Sound.Stop({ sId: argobj.pid, sEvent: "click" });
				if(oCurObj.data.sSoundClick!="none")
				{
					CL.Sound.Play({ sId: argobj.pid, sEvent: "click", sType: oCurObj.data.sSoundClick, sFile: oCurObj.data.sSoundClickFile })
				}
				return true;
			}

function GetProperty(argobj)
			{
				return CLO[argobj.pid].GetProperty(argobj);
			}

function Click(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				if(oCurObj.data.sState=="disabled") return false;
				if(oCurObj.data.sRole=="close")
				{
					if(oCurObj.data.bDisplayConfirm)
					{
					if(confirm(CLO[argobj.pid].jxParams.children("closemodule_msg").text()))
						{
							window.close();
						}
					}
					else
					{
						window.close();
					}
				}
				else
				{
					CLO[argobj.pid].CallMethod({ sMethod: "Navigate" });
				}
				return true;
			}

function Constructor(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var sQuestionId = argobj.pid;
				var bDefer = false;
				switch(oCurObj.display)
				{
					case "none":
					{
						bDefer = true;
						CL.Event.Subscribe({ aList: [ { sId: argobj.pid, sEvtName: "EVENT_OBJECT_SHOW" } ] });
						break;
					}
					case "time":
					case "frame":
					case "slide":
					{
						if(oCurObj.iBegin!=null && +oCurObj.iBegin!=0)
						{
							bDefer = true;
							CL.Event.Subscribe({ aList: [ { sId: argobj.pid, sEvtName: "EVENT_OBJECT_SHOW" } ] });
						}
						break;
					}
				}
				var jBtn = jCurObjDiv.find("#" + argobj.pid + "_BUTTON_submit");
				jBtn.parent().height(jBtn.height());
				oCurObj.data =
				{
					bCreated: false,
					bCredit: (oCurObj.jxParams.children("q_scored").text()=="yes"),
					bNoMoreAttempts: false,
					bShowCorrect: (oCurObj.jxParams.children("q_showright").text()=="yes"),
					bFeedback: (oCurObj.jxParams.children("q_fb_switch").text()=="yes"),
					bModal: (oCurObj.jxParams.find("modal").text()=="yes"),
					bSkip: (oCurObj.jxParams.find("q_skip").text()!="none"),
					bTimer: (oCurObj.jxParams.children("q_timer_switch").text()=="yes"),
					bTimeout: false,
					jxObjectives: oCurObj.jxParams.find("q_scoreboards > item"),
					nBaseScore: +oCurObj.jxParams.children("q_weight").text(),
					sMode: "init",
					sType: oCurObj.jxParams.children("question_type").text(),
					sSoundSuccess: oCurObj.jxParams.children("sound_success").text(),
					sSoundFailure: oCurObj.jxParams.children("sound_failure").text(),
					sSoundClick: oCurObj.jxParams.children("sound_click").text(),
					sSoundOver: oCurObj.jxParams.children("sound_over").text()
				};
				var jAudioContainer = jCurObjDiv.find(".cl-audio-files-list");
				if(oCurObj.data.sSoundClick!="none")
				{
					oCurObj.data.jAudioClick = CL.Sound.Append({ oContainer: jAudioContainer, sId: argobj.pid + "_AUDIO1", sType: oCurObj.data.sSoundClick, sFile: oCurObj.jxParams.children("sound_click_file_custom").text() });
				}
				if(oCurObj.data.sSoundOver!="none")
				{
					oCurObj.data.jAudioOver = CL.Sound.Append({ oContainer: jAudioContainer, sId: argobj.pid + "_AUDIO2", sType: oCurObj.data.sSoundOver, sFile: oCurObj.jxParams.children("sound_over_file_custom").text() });
				}
				if(oCurObj.data.sSoundSuccess!="none")
				{
					oCurObj.data.jAudioSuccess = CL.Sound.Append({ oContainer: jAudioContainer, sId: argobj.pid + "_AUDIO3", sType: oCurObj.data.sSoundSuccess, sFile: oCurObj.jxParams.children("sound_success_file_custom").text() });
				}
				if(oCurObj.data.sSoundFailure!="none")
				{
					oCurObj.data.jAudioFailure = CL.Sound.Append({ oContainer: jAudioContainer, sId: argobj.pid + "_AUDIO4", sType: oCurObj.data.sSoundFailure, sFile: oCurObj.jxParams.children("sound_failure_file_custom").text() });
				}
				switch(oCurObj.data.sType)
				{
					case "true-false":
					{
						oCurObj.data.sScoringType = oCurObj.jxParams.children("q_scoring_type_tf").text();
						oCurObj.data.bShuffle = (oCurObj.jxParams.children("q_elements_order").text()=="random");
						break;
					}
					case "choice":
					{
						oCurObj.data.sScoringType = oCurObj.jxParams.children("q_scoring_type_choice").text();
						oCurObj.data.jxVars = oCurObj.jxParams.find("q_variants_choice > item");
						oCurObj.data.bShuffle = (oCurObj.jxParams.children("q_shuffle_choice").text()=="yes");
						break;
					}
					case "select":
					{
						oCurObj.data.sScoringType = oCurObj.jxParams.children("q_scoring_type_select").text();
						oCurObj.data.jxVars = oCurObj.jxParams.find("q_variants_select > item");
						oCurObj.data.bShuffle = (oCurObj.jxParams.children("q_shuffle_select").text()=="yes");
						break;
					}
					case "order":
					{
						oCurObj.data.sScoringType = oCurObj.jxParams.children("q_scoring_type_match").text();
						oCurObj.data.jxVars = oCurObj.jxParams.find("q_variants_order > item");
						oCurObj.data.bShuffle = true;
						break;
					}
					case "numeric":
					{
						oCurObj.data.sScoringType = oCurObj.jxParams.children("q_scoring_type_inputs").text();
						oCurObj.data.jxVars = oCurObj.jxParams.find("q_variants_numeric > item");
						oCurObj.data.sOp = oCurObj.jxParams.children("q_var_logic_numeric").text();
						break;
					}
					case "fib":
					{
						oCurObj.data.sScoringType = oCurObj.jxParams.children("q_scoring_type_inputs").text();
						oCurObj.data.jxVars = oCurObj.jxParams.find("q_variants_fib > item");
						oCurObj.data.sOp = oCurObj.jxParams.children("q_var_logic_fib").text();
						break;
					}
					case "oto":
					{
						oCurObj.data.sScoringType = oCurObj.jxParams.children("q_scoring_type_match").text();
						oCurObj.data.jxVars = oCurObj.jxParams.find("q_variants_oto > item");
						break;
					}
				}
				if(CLZ.oStore[sQuestionId]!=null)
				{
					switch(CLZ.oStore[sQuestionId].u)
					{
						case "c":
						{
							switch(oCurObj.jxParams.children("q_replay").text())
							{
								case "none_alert":
								{
									alert(oCurObj.jxParams.children("q_browse_alert").text());
									// no break
								}
								case "none_silent":
								{
									oCurObj.data.sMode = "browse";
									oCurObj.data.bNoMoreAttempts = true;
									break;
								}
								case "allow_alert":
								{
									alert(oCurObj.jxParams.children("q_secondrun_alert").text());
									// no break
								}
								case "allow_silent":
								{
									CLZ.oStore[sQuestionId] = {};
									CLZ.oStore[sQuestionId] = null;
									if(oCurObj.data.bCredit)
									{
										for(var j=0; j<oCurObj.data.jxObjectives.length; j++)
										{
											CL.SCO.RemoveScoreFromObjective({ sId: $(oCurObj.data.jxObjectives[j]).children("q_board").text(), sSrcId: argobj.pid });
										}
									}
									break;
								}
								case "allow_confirm":
								{
									if(confirm(oCurObj.jxParams.children("q_secondrun_confirm").text()))
									{
										CLZ.oStore[sQuestionId] = {};
										CLZ.oStore[sQuestionId] = null;
										if(oCurObj.data.bCredit)
										{
											for(var j=0; j<oCurObj.data.jxObjectives.length; j++)
											{
												CL.SCO.RemoveScoreFromObjective({ sId: $(oCurObj.data.jxObjectives[j]).children("q_board").text(), sSrcId: argobj.pid });
											}
										}
									}
									else
									{
										oCurObj.data.sMode = "browse";
										oCurObj.data.bNoMoreAttempts = true;
									}
									break;
								}
							}
							break;
						}
						case "i":
						{
							switch(oCurObj.jxParams.children("q_continue").text())
							{
								case "restart_alert":
								{
									alert(oCurObj.jxParams.children("q_restart_alert").text());
									// no break
								}
								case "restart_silent":
								{
									CLZ.oStore[sQuestionId] = {};
									CLZ.oStore[sQuestionId] = null;
									if(oCurObj.data.bCredit)
									{
										for(var j=0; j<oCurObj.data.jxObjectives.length; j++)
										{
											CL.SCO.RemoveScoreFromObjective({ sId: $(oCurObj.data.jxObjectives[j]).children("q_board").text(), sSrcId: argobj.pid });
										}
									}
									break;
								}
								case "restore_alert":
								{
									alert(oCurObj.jxParams.children("q_restore_alert").text());
									// no break
								}
								case "restore_silent":
								{
									oCurObj.data.sMode = "continue";
									break;
								}
								case "restore_confirm":
								{
									if(confirm(oCurObj.jxParams.children("q_restore_confirm").text()))
									{
										oCurObj.data.sMode = "continue";
									}
									else
									{
										CLZ.oStore[sQuestionId] = {};
										CLZ.oStore[sQuestionId] = null;
										if(oCurObj.data.bCredit)
										{
											for(var j=0; j<oCurObj.data.jxObjectives.length; j++)
											{
												CL.SCO.RemoveScoreFromObjective({ sId: $(oCurObj.data.jxObjectives[j]).children("q_board").text(), sSrcId: argobj.pid });
											}
										}
									}
									break;
								}
							}
							break;
						}
						default:
						{
							CLZ.oStore[sQuestionId] = {};
							CLZ.oStore[sQuestionId] = null;
							if(oCurObj.data.bCredit)
							{
								for(var j=0; j<oCurObj.data.jxObjectives.length; j++)
								{
									CL.SCO.RemoveScoreFromObjective({ sId: $(oCurObj.data.jxObjectives[j]).children("q_board").text(), sSrcId: argobj.pid });
								}
							}
							break;
						}
					}
				}
				switch(oCurObj.data.sMode)
				{
					case "init":
					{
						// create storage
						CLZ.oStore[sQuestionId] =
						{
							A:
							[
								{
									s: 0,
									d: 0,
									u: "i"
								}
							],
							O: [],
							P: [],
							R: [],
							s: (new Date()).valueOf(),
							y: CLD.CLTypeToAbbr[oCurObj.data.sType],
							u: "i"
						};
						if(oCurObj.data.bCredit)
						{
							CLZ.oStore[sQuestionId].J = [];
							oCurObj.data.jxObjectives.each(function () { CLZ.oStore[sQuestionId].J.push( $(this).children("q_board").text() ); });
						}
						if(oCurObj.data.bTimer)
						{
							var iTimer = +oCurObj.jxParams.children("q_timer").text();
							if(isNaN(iTimer))
							{
								oCurObj.data.bTimer = false;
							}
							else
							{
								CLZ.oStore[sQuestionId].t = iTimer;
								CLZ.oStore[sQuestionId].e = 0;
							}
						}
						switch(oCurObj.data.sType)
						{
							case "true-false":
							{
								if(oCurObj.data.sScoringType=="plain" || oCurObj.data.sScoringType=="byobj" && oCurObj.jxParams.children("q_eval").text()!="byvar")
								{
									CLZ.oStore[sQuestionId].P.push( (oCurObj.jxParams.children("q_eval").text()=="true") ? 1 : 0 );
								}
								if(oCurObj.data.bShuffle)
								{
									var aShuffled = [];
									for(var i=0; i<2; i++) aShuffled[i] = CLTOOLS.Dice({ iMax: 2, bIncludeMax: false, aUsed: aShuffled, iMaxTries: 1000 });
									for(var i=0; i<2; i++) CLZ.oStore[sQuestionId].O.push(aShuffled[i]);
								}
								else
								{
									CLZ.oStore[sQuestionId].O = [ 0, 1 ];
								}
								// alwaus 1 attempt
								break;
							}
							case "choice":
							{
								if(oCurObj.data.sScoringType=="fixed" || oCurObj.data.sScoringType=="plain" || oCurObj.data.sScoringType=="regressive" || oCurObj.data.sScoringType=="byobj") // fixed - 2.7 legacy
								{
									// 3.1.2.1
									for(var i=1; i<=oCurObj.data.jxVars.length; i++)
									{
										if($(oCurObj.data.jxVars[i-1]).children("q_right").text()=="yes")
										{
											CLZ.oStore[sQuestionId].P.push(i);
											break;
										}
									}
								}
								if(oCurObj.data.bShuffle)
								{
									var aShuffled = [];
									for(var i=0; i<oCurObj.data.jxVars.length; i++) aShuffled.push( CLTOOLS.Dice({ iMax: oCurObj.data.jxVars.length, bIncludeMax: false, aUsed: aShuffled, iMaxTries: 1000 }) );
									for(var i=0; i<oCurObj.data.jxVars.length; i++) CLZ.oStore[sQuestionId].O.push(aShuffled[i]+1);
								}
								else
								{
									for(var i=1; i<=oCurObj.data.jxVars.length; i++)
									{
										CLZ.oStore[sQuestionId].O.push(i);
									}
								}
								var iMaxAttepmts = 1;
								if(oCurObj.data.sScoringType!="byvar")
								{
									if(oCurObj.jxParams.children("q_attempts_type").text()=="auto")
									{
										iMaxAttepmts = oCurObj.data.jxVars.length - 1;
									}
									else
									{
										iMaxAttepmts = +oCurObj.jxParams.children("q_attempts").text();
									}
								}
								for(var i=1; i<iMaxAttepmts; i++)
								{
									CLZ.oStore[sQuestionId].A.push({ u: "n" });
								}
								break;
							}
							case "select":
							{
								if(oCurObj.data.sScoringType=="fixed" || oCurObj.data.sScoringType=="plain" || oCurObj.data.sScoringType=="regressive" || oCurObj.data.sScoringType=="byobj") // fixed - 2.7 legacy
								{
									for(var i=1; i<=oCurObj.data.jxVars.length; i++)
									{
										if($(oCurObj.data.jxVars[i-1]).children("q_right").text()=="yes")
										{
											CLZ.oStore[sQuestionId].P.push(i);
										}
									}
								}
								if(oCurObj.data.bShuffle)
								{
									var aShuffled = [];
									for(var i=0; i<oCurObj.data.jxVars.length; i++) aShuffled.push( CLTOOLS.Dice({ iMax: oCurObj.data.jxVars.length, bIncludeMax: false, aUsed: aShuffled, iMaxTries: 1000 }) );
									for(var i=0; i<oCurObj.data.jxVars.length; i++) CLZ.oStore[sQuestionId].O.push(aShuffled[i]+1);
								}
								else
								{
									for(var i=1; i<=oCurObj.data.jxVars.length; i++)
									{
										CLZ.oStore[sQuestionId].O.push(i);
									}
								}
								var iMaxAttepmts = 1;
								if(oCurObj.data.sScoringType=="fixed" || oCurObj.data.sScoringType=="plain" || oCurObj.data.sScoringType=="regressive" || oCurObj.data.sScoringType=="byobj") // fixed - 2.7 legacy
								{
									if(oCurObj.jxParams.children("q_attempts_type").text()=="auto")
									{
										iMaxAttepmts = oCurObj.data.jxVars.length - 1;
									}
									else
									{
										iMaxAttepmts = +oCurObj.jxParams.children("q_attempts").text();
									}
								}
								for(var i=1; i<iMaxAttepmts; i++)
								{
									CLZ.oStore[sQuestionId].A.push({ u: "n" });
								}
								break;
							}
							case "order":
							{
								for(var i=1; i<=oCurObj.data.jxVars.length; i++)
								{
									CLZ.oStore[sQuestionId].P.push(i);
								}
								var aShuffled = [];
								for(var i=0; i<oCurObj.data.jxVars.length; i++) aShuffled.push( CLTOOLS.Dice({ iMax: oCurObj.data.jxVars.length, bIncludeMax: false, aUsed: aShuffled, iMaxTries: 1000 }) );
								for(var i=0; i<oCurObj.data.jxVars.length; i++)
								{
									CLZ.oStore[sQuestionId].O.push(aShuffled[i]+1);
									CLZ.oStore[sQuestionId].R.push(aShuffled[i]+1);
								}
								var iMaxAttepmts = 1;
								if(oCurObj.data.sScoringType=="fixed" || oCurObj.data.sScoringType=="plain" || oCurObj.data.sScoringType=="regressive" || oCurObj.data.sScoringType=="byobj") // fixed - 2.7 legacy
								{
									if(oCurObj.jxParams.children("q_attempts_type").text()=="auto")
									{
										iMaxAttepmts = oCurObj.data.jxVars.length - 1;
									}
									else
									{
										iMaxAttepmts = +oCurObj.jxParams.children("q_attempts").text();
									}
								}
								for(var i=1; i<iMaxAttepmts; i++)
								{
									CLZ.oStore[sQuestionId].A.push({ u: "n" });
								}
								break;
							}

							case "numeric":
							case "fib":
							{
								var oPattern;
								var aConds;
								var jVar;
								var jConds;
								for(var i=1; i<=oCurObj.data.jxVars.length; i++)
								{
									jVar = $(oCurObj.data.jxVars[i-1]);
									oPattern = { sOp: jVar.children("q_cond_logic").text() };
									jConds = jVar.find("q_conditions > item");
									aConds = [];
									if(oCurObj.data.sType=="fib")
									{
										for(var j=0; j<jConds.length; j++)
										{
											aConds.push({ op: $(jConds[j]).children("q_condition").text(), val: $(jConds[j]).children("q_variant").text(), cs: ($(jConds[j]).children("q_case").text()=="yes") })
										}
									}
									else
									{
										for(var j=0; j<jConds.length; j++)
										{
											aConds.push({ op: $(jConds[j]).children("q_condition").text(), val: +$(jConds[j]).children("q_variant").text() })
										}
									}
									oPattern.aConds = aConds.slice(0);
									CLZ.oStore[sQuestionId].P.push(oPattern);
									CLZ.oStore[sQuestionId].R.push("");
								}
								for(var i=1; i<=oCurObj.data.jxVars.length; i++)
								{
									CLZ.oStore[sQuestionId].O.push(i);
								}
								var iMaxAttepmts = 1;
								if(oCurObj.jxParams.children("q_attempts_type").text()=="auto")
								{
									iMaxAttepmts = oCurObj.data.jxVars.length - 1;
								}
								else
								{
									iMaxAttepmts = +oCurObj.jxParams.children("q_attempts").text();
								}
								for(var i=1; i<iMaxAttepmts; i++)
								{
									CLZ.oStore[sQuestionId].A.push({ u: "n" });
								}
								break;
							}
							case "oto":
							{
								for(var i=1; i<=oCurObj.data.jxVars.length; i++)
								{
									CLZ.oStore[sQuestionId].P.push(i);
								}
								var aShuffled = [];
								for(var i=0; i<oCurObj.data.jxVars.length; i++) aShuffled.push( CLTOOLS.Dice({ iMax: oCurObj.data.jxVars.length, bIncludeMax: false, aUsed: aShuffled, iMaxTries: 1000 }) );
								for(var i=0; i<oCurObj.data.jxVars.length; i++)
								{
									CLZ.oStore[sQuestionId].O.push(aShuffled[i]+1);
									CLZ.oStore[sQuestionId].R.push(aShuffled[i]+1);
								}
								var iMaxAttepmts = 1;
								if(oCurObj.data.sScoringType=="fixed" || oCurObj.data.sScoringType=="plain" || oCurObj.data.sScoringType=="regressive" || oCurObj.data.sScoringType=="byobj") // fixed - 2.7 legacy
								{
									if(oCurObj.jxParams.children("q_attempts_type").text()=="auto")
									{
										iMaxAttepmts = oCurObj.data.jxVars.length - 1;
									}
									else
									{
										iMaxAttepmts = +oCurObj.jxParams.children("q_attempts").text();
									}
								}
								for(var i=1; i<iMaxAttepmts; i++)
								{
									CLZ.oStore[sQuestionId].A.push({ u: "n" });
								}
								break;
							}

						}
						break;
					}
					case "browse":
					case "continue":
					{
						break;
					}
				}
				switch(oCurObj.data.sType)
				{
					case "true-false":
					{
						var jVarContainer = jCurObjDiv.find(".cl-q-vars");
						var jVarDiv;
						if(oCurObj.jxParams.children("q_elements_layout").text()=="v")
						{
							for(var i=0; i<CLZ.oStore[sQuestionId].O.length; i++)
							{
								iIdx = +CLZ.oStore[sQuestionId].O[i];
								jVarDiv = $("#" + argobj.pid + "_Q_VAR_" + iIdx);
								if(oCurObj.data.bShuffle)
								{
									jVarContainer.append( jVarDiv );
								}
								if(CLZ.oStore[sQuestionId].R.length!=0)
								{
									if(+CLZ.oStore[sQuestionId].R[0]==iIdx)
									{
										jVarDiv.removeClass("cl-var-idle").addClass("cl-var-selected");
									}
								}
								if(oCurObj.data.sMode=="browse")
								{
									jVarDiv.addClass("cl-var-disabled");
								}
								else
								{ 	// bind events
									if(!jVarDiv.hasClass("cl-var-clickable"))
									{
									if(CL.bTouch)
									{
										jVarDiv.addClass("cl-var-clickable unselectable").on("click", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); });
									}
									else
									{
										jVarDiv.addClass("cl-var-clickable unselectable").on("mouseover", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); }).on("mouseout", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); }).on("click", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); });
									}
								}
							}
						}
						}
						else
						{
							var jParent = jCurObjDiv.find(".cl-q-vars-tf-h-td");
							for(var i=0; i<CLZ.oStore[sQuestionId].O.length; i++)
							{
								iIdx = +CLZ.oStore[sQuestionId].O[i];
								jVarDiv = $("#" + argobj.pid + "_Q_VAR_" + iIdx);
								$(jParent[i]).append( jVarDiv );
								if(CLZ.oStore[sQuestionId].R.length!=0)
								{
									if(+CLZ.oStore[sQuestionId].R[0]==iIdx)
									{
										jVarDiv.removeClass("cl-var-idle").addClass("cl-var-selected");
									}
								}
								if(oCurObj.data.sMode=="browse")
								{
									jVarDiv.addClass("cl-var-disabled");
								}
								else
								{ 	// bind events
									if(!jVarDiv.hasClass("cl-var-clickable"))
									{
									if(CL.bTouch)
									{
										jVarDiv.addClass("cl-var-clickable").on("click", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); });
									}
									else
									{
										jVarDiv.addClass("cl-var-clickable").on("mouseover", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); }).on("mouseout", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); }).on("click", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); });
									}
								}
							}
						}
						}
						break;
					}
					case "choice":
					{
						// restoring variants area
						var jVarContainer = jCurObjDiv.find(".cl-q-vars");
						var jVarDiv;
						for(var i=0; i<CLZ.oStore[sQuestionId].O.length; i++)
						{
							iIdx = +CLZ.oStore[sQuestionId].O[i];
							jVarDiv = $("#" + argobj.pid + "_Q_VAR_" + iIdx);
							if(oCurObj.data.bShuffle)
							{
								jVarContainer.append( jVarDiv );
							}
							if(CLZ.oStore[sQuestionId].R.length!=0)
							{
								if(+CLZ.oStore[sQuestionId].R[0]==iIdx)
								{
									jVarDiv.removeClass("cl-var-idle").addClass("cl-var-selected");
								}
							}
							if(oCurObj.data.sMode=="browse")
							{
								jVarDiv.addClass("cl-var-disabled");
							}
							else
							{ 	// bind events
								if(!jVarDiv.hasClass("cl-var-clickable"))
								{
								if(CL.bTouch)
								{
									jVarDiv.addClass("cl-var-clickable unselectable").on("click", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); });
								}
								else
								{
									jVarDiv.addClass("cl-var-clickable unselectable").on("mouseover", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); }).on("mouseout", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); }).on("click", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); });
								}
							}
						}
						}
						if(oCurObj.data.sMode=="browse")
						{
							if(CLZ.oStore[sQuestionId].sSkip!=null)
							{
								if(CLZ.oStore[sQuestionId].sSkip=="f")
								{
									jVarContainer.find(".cl-q-question-goal").html(oCurObj.jxParams.children("q_goal_failed").text());
								}
								else
								{
									jVarContainer.find(".cl-q-question-goal").html(oCurObj.jxParams.children("q_goal_skip").text());
								}
							}
							else
							{
								jVarContainer.find(".cl-q-question-goal").html(oCurObj.jxParams.children("q_goal_last").text());
							}
						}
						break;
					}
					case "select":
					{
						var jVarContainer = jCurObjDiv.find(".cl-q-vars");
						var jVarDiv;
						for(var i=0; i<CLZ.oStore[sQuestionId].O.length; i++)
						{
							iIdx = +CLZ.oStore[sQuestionId].O[i];
							jVarDiv = $("#" + argobj.pid + "_Q_VAR_" + iIdx);
							if(oCurObj.data.bShuffle)
							{
								jVarContainer.append( jVarDiv );
							}
							if(CLZ.oStore[sQuestionId].R.length!=0)
							{
								if($.inArray(iIdx, CLZ.oStore[sQuestionId].R)!=-1)
								{
									jVarDiv.removeClass("cl-var-idle").addClass("cl-var-selected");
								}
							}
							if(oCurObj.data.sMode=="browse")
							{
								jVarDiv.addClass("cl-var-disabled");
							}
							else
							{ 	// bind events
								if(!jVarDiv.hasClass("cl-var-clickable"))
								{
								if(CL.bTouch)
								{
									jVarDiv.addClass("cl-var-clickable unselectable").on("click", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); });
								}
								else
								{
									jVarDiv.addClass("cl-var-clickable unselectable").on("mouseover", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); }).on("mouseout", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); }).on("click", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); });
								}
							}
						}
						}
						break;
					}
					case "order":
					{
						var jVarContainer = jCurObjDiv.find(".cl-q-vars");
						var jVarDiv;
						for(var i=0; i<CLZ.oStore[sQuestionId].O.length; i++)
						{
							iIdx = +CLZ.oStore[sQuestionId].O[i];
							jVarDiv = $("#" + argobj.pid + "_Q_VAR_" + iIdx);
							jVarContainer.attr({ "id": argobj.pid + "_VC" }).append(jVarDiv);
							if(oCurObj.data.sMode=="browse")
							{
								jVarDiv.addClass("cl-var-disabled");
							}
							else
							{ 	// bind events
								jVarDiv.addClass("unselectable cl-drag").draggable({ snap: false, scope: argobj.pid, refreshPositions: true, containment: $("#" + argobj.pid + "_VC"), axis: "y", stack: ".cl-var" }).droppable({ accept: ".cl-var", scope: argobj.pid }).on("drop", function(event, ui) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: event, elem: this, bullet: ui.draggable } }) });
							}
						}
						break;
					}
					case "numeric":
					case "fib":
					{
						var jVarDivs = jCurObjDiv.find(".cl-var");
						var jInput;
						for(var i=0; i<CLZ.oStore[sQuestionId].R.length; i++)
						{
							jInput = $(jVarDivs[i]).find(".cl-var-input");
							if(CLZ.oStore[sQuestionId].R[i]!="")
							{
								jInput.val(CLZ.oStore[sQuestionId].R[i]);
							}
							if(oCurObj.data.sMode=="browse")
							{
								$(jVarDivs[i]).addClass("cl-var-disabled");
							}
							else
							{ 	// bind events
								jInput.on("keyup", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); }).on("blur", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); });
							}
						}
						break;
					}
					case "oto":
					{
						var jVarDivs = jCurObjDiv.find(".cl-var-bullet");
						oCurObj.data.jNests = [];
						jVarDivs.each(function () { oCurObj.data.jNests.push($(this).parent()); });
						var jVar;
						var iIdx;
						for(var i=0; i<CLZ.oStore[sQuestionId].O.length; i++)
						{
							iIdx = CLZ.oStore[sQuestionId].O[i];
							jVar = jCurObjDiv.find(".cl-var-bullet[data-vid='"+ iIdx +"']");
							jVar.appendTo(oCurObj.data.jNests[i]);
							if(oCurObj.data.sMode=="browse")
							{
								jVar.addClass("cl-var-disabled");
							}
							else
							{ 	// bind events
								jVar.addClass("unselectable cl-drag").draggable({ snap: false, refreshPositions: true, containment: jCurObjDiv.find(".cl-q-vars"), axis: "y", stack: ".cl-var-bullet" }).droppable({ accept: ".cl-var-bullet" }).on("drop", function(event, ui) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: event, elem: this, bullet: ui.draggable } }) });
							}
						}
						break;
					}
				}
				var iAttemptsLeft = 0;
				for(var i=0; i<CLZ.oStore[sQuestionId].A.length; i++)
				{
					if(CLZ.oStore[sQuestionId].A[i].u=="c") continue;
					iAttemptsLeft = CLZ.oStore[sQuestionId].A.length - i;
					break;
				}
				jCurObjDiv.find(".cl-q-attempts-num").html( iAttemptsLeft );
				if(oCurObj.data.bTimer)
				{
					oCurObj.CallMethod({ sMethod: "Timer", oMethodArgs: { action: "init" } });
				}
				if(CL.bTouch)
				{
					jCurObjDiv.find(".cl-btn, .cl-q-feedback-btn").on("click", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); });
				}
				else
				{
					jCurObjDiv.find(".cl-btn, .cl-q-feedback-btn").on("mouseover", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); }).on("mouseout", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); }).on("click", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); });
				}
				if(oCurObj.data.sMode=="init")
				{
					oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { target: "submit", action: "hide" } });
					if(oCurObj.data.bSkip)
					{
						oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { target: "skip", action: "show" } });
					}
				}
				else if(oCurObj.data.sMode=="continue")
				{
					oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { target: "submit", action: "hide" } });
					oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { target: "skip", action: "remove" } });
				}
				else if(oCurObj.data.sMode=="browse")
				{
					oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { target: "submit", action: "remove" } });
					oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { target: "skip", action: "remove" } });
				}
				oCurObj.data.bCreated = true;
				if(!bDefer)
				{
					oCurObj.CallMethod({ sMethod: "Display" });
				}
				return true;
			}

function Display(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				if(oCurObj==null) return false;
				if(oCurObj.data.bModal && (oCurObj.data.sMode!="browse"))
				{
					CL.Modal({ bOn: true, sSrcId: argobj.pid });
				}
				return true;
			}

function HandleEvt(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				if(oCurObj==null) return false;
				switch(argobj.evt)
				{
					case "EVENT_OBJECT_SHOW":
					{
						if(!oCurObj.data.bCreated) return false;
						oCurObj.CallMethod({ sMethod: "Display" });
						break;
					}
				}
				return true;
			}

function UIEvent(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var sQuestionId = argobj.pid;
				var jElem;
				if($(argobj.elem).hasClass("cl-var-spot-td"))
				{
					jElem = $(argobj.elem).parents(".cl-var:first");
				}
				else
				{
					jElem = $(argobj.elem);
				}
				if(jElem.hasClass("cl-var") || jElem.hasClass("cl-var-bullet"))
				{
					if(jElem.hasClass("cl-var-disabled")) return false;
					switch(argobj.evt.type)
					{
						case "mouseover":
						{
							if(CLZ.bSoundOn && oCurObj.data.sSoundOver!="none")
							{
								CL.Sound.CLPlay({ jPlayer: oCurObj.data.jAudioOver });
							}
							break;
						}
						case "mouseout":
						{
							if(CLZ.bSoundOn && oCurObj.data.sSoundOver!="none")
							{
								CL.Sound.CLStop({ jPlayer: oCurObj.data.jAudioOver });
							}
							break;
						}
						case "click":
						{
							if(CLZ.bSoundOn && oCurObj.data.sSoundClick!="none")
							{
								CL.Sound.CLPlay({ jPlayer: oCurObj.data.jAudioClick });
							}
							// remove skip button if any
							if(!oCurObj.data.bSkipRemoved)
							{
								oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { target: "skip", action: "remove" } });
								oCurObj.data.bSkipRemoved = true;
							}
							// hide feedback if any
							oCurObj.CallMethod({ sMethod: "Feedback", oMethodArgs: { hide: true } });
							switch(oCurObj.data.sType)
							{
								case "true-false":
								case "choice":
								{
									if(jElem.hasClass("cl-var-selected")) return false;
									jCurObjDiv.find(".cl-var").removeClass("cl-var-selected").addClass("cl-var-idle");  // clear all selected
									jElem.removeClass("cl-var-idle").addClass("cl-var-selected");
									CLZ.oStore[sQuestionId].R[0] = +jElem.attr("data-vid");
									break;
								}
								case "select":
								{
									var iNum = +jElem.attr("data-vid");
									var iIdx = $.inArray(iNum, CLZ.oStore[sQuestionId].R);
									if(jElem.hasClass("cl-var-selected"))
									{
										jElem.removeClass("cl-var-selected").addClass("cl-var-idle");
										if(iIdx!=-1)
										{
											CLZ.oStore[sQuestionId].R.splice(iIdx,1);
										}
									}
									else
									{
										jElem.removeClass("cl-var-idle").addClass("cl-var-selected");
										if(iIdx==-1)
										{
											CLZ.oStore[sQuestionId].R.push(iNum);
										}
									}
									break;
								}
							}
							oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { target: "submit", action: ((CLZ.oStore[sQuestionId].R.length>0) ? "show" : "hide") } });
							break;
						}
						case "drop":
						{ // order
							argobj.evt.stopImmediatePropagation();
							var iTargetIdx = +jElem.attr("data-vid");
							var iBulletIdx = +$(argobj.bullet).attr("data-vid");
							var aNewOrder = [];
							if(!oCurObj.data.bSkipRemoved)
							{
								oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { target: "skip", action: "remove" } });
								oCurObj.data.bSkipRemoved = true;
							}
							for(var i=0; i<CLZ.oStore[sQuestionId].O.length; i++)
							{
								if(CLZ.oStore[sQuestionId].O[i]==iTargetIdx)
								{
									aNewOrder.push(iBulletIdx);
								}
								else if(CLZ.oStore[sQuestionId].O[i]==iBulletIdx)
								{
									aNewOrder.push(iTargetIdx);
								}
								else
								{
									aNewOrder.push(CLZ.oStore[sQuestionId].O[i]);
								}
							}
							if(oCurObj.data.sType=="order")
							{
								var jParent = jElem.parent();
								CLZ.oStore[sQuestionId].O = [];
								CLZ.oStore[sQuestionId].R = [];
								for(var i=0; i<aNewOrder.length; i++)
								{
									$("#" + argobj.pid + "_Q_VAR_" + aNewOrder[i]).css({ "top": "", "z-index": "" }).appendTo(jParent);
									CLZ.oStore[sQuestionId].O.push(aNewOrder[i]);
									CLZ.oStore[sQuestionId].R.push(aNewOrder[i]);
								}
							}
							else if(oCurObj.data.sType=="oto")
							{
								CLZ.oStore[sQuestionId].O = [];
								CLZ.oStore[sQuestionId].R = [];
								for(var i=0; i<aNewOrder.length; i++)
								{
									jCurObjDiv.find(".cl-var-bullet[data-vid='"+ aNewOrder[i] +"']").css({ "top": "", "z-index": "" }).appendTo(oCurObj.data.jNests[i]);
									CLZ.oStore[sQuestionId].O.push(aNewOrder[i]);
									CLZ.oStore[sQuestionId].R.push(aNewOrder[i]);
								}
							}
							oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { target: "submit", action: "show" } });
							break;
						}
					}
				}
				else if(jElem.hasClass("cl-btn"))
				{
					switch(argobj.evt.type)
					{
						case "mouseover":
						{
							if(CLZ.bSoundOn && oCurObj.data.sSoundOver!="none")
							{
								CL.Sound.CLPlay({ jPlayer: oCurObj.data.jAudioOver });
							}
							break;
						}
						case "mouseout":
						{
							if(CLZ.bSoundOn && oCurObj.data.sSoundOver!="none")
							{
								CL.Sound.CLStop({ jPlayer: oCurObj.data.jAudioOver });
							}
							break;
						}
						case "click":
						{
							if(CLZ.bSoundOn && oCurObj.data.sSoundClick!="none")
							{
								CL.Sound.CLPlay({ jPlayer: oCurObj.data.jAudioClick });
							}
							if(jElem.attr("data-action")=="submit")
							{
								oCurObj.CallMethod({ sMethod: "Submit" });
							}
							else if(jElem.attr("data-action")=="skip")
							{
								oCurObj.CallMethod({ sMethod: "Skip" });
							}
							jElem.hide();
							break;
						}
					}
				}
				else if(jElem.hasClass("cl-var-input"))
				{
					var iIdx = +jElem.attr("data-vid") - 1;
					switch(argobj.evt.type)
					{
						case "blur":
						{
							CLZ.oStore[sQuestionId].R[iIdx] = jElem.val();
							break;
						}
						case "keyup":
						{
							var sValue = jElem.val();
							if(oCurObj.data.sType=="numeric")
							{
								sValue = CL.Q.FixNumeric({ sText: sValue });
							}
							jElem.val(sValue);
							var iIdx = +jElem.attr("data-vid") - 1;
							CLZ.oStore[sQuestionId].R[iIdx] = sValue;
							break;
						}
					}
					// remove skip button if any
					if(!oCurObj.data.bSkipRemoved)
					{
						oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { target: "skip", action: "remove" } });
						oCurObj.data.bSkipRemoved = true;
					}
					oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { target: "submit", action: "show" } });
				}
				else if(jElem.hasClass("cl-q-feedback-btn"))
				{
					switch(argobj.evt.type)
					{
						case "mouseover":
						{
							if(CLZ.bSoundOn && oCurObj.data.sSoundOver!="none")
							{
								CL.Sound.CLPlay({ jPlayer: oCurObj.data.jAudioOver });
							}
							break;
						}
						case "mouseout":
						{
							if(CLZ.bSoundOn && oCurObj.data.sSoundOver!="none")
							{
								CL.Sound.CLStop({ jPlayer: oCurObj.data.jAudioOver });
							}
							break;
						}
						case "click":
						{
							if(CLZ.bSoundOn && oCurObj.data.sSoundClick!="none")
							{
								CL.Sound.CLPlay({ jPlayer: oCurObj.data.jAudioClick });
							}
							oCurObj.CallMethod({ sMethod: "Feedback", oMethodArgs: { hide: true } });
							break;
						}
					}
				}
				return true;
			}

function Skip(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var sQuestionId = argobj.pid;
				var sMsg = "";
				var bFail = false;
				switch(oCurObj.jxParams.children("q_skip").text())
				{
					case "skip":
					{
						sMsg = oCurObj.jxParams.children("q_goal_skip").text();
						CLZ.oStore[sQuestionId].sSkip = "s";
						break;
					}
					case "fail_confirm":
					{
						if(!confirm(oCurObj.jxParams.children("q_fail_confirm").text()))
						{
							return false;
						}
					} // break omitted intentionally
					case "fail_silent":
					{
						bFail = true;
						sMsg = oCurObj.jxParams.children("q_goal_failed").text();
						CLZ.oStore[sQuestionId].sSkip = "f";
						break;
					}
				}
				jCurObjDiv.find(".cl-q-question-goal").html(sMsg);
				oCurObj.FireEvent({ sName: "onSkipQuestion" })
				oCurObj.CallMethod({ sMethod: "CloseQuestion", oMethodArgs: { bFail: bFail, bSkip: true } });
				return true;
			}

function Submit(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var sQuestionId = argobj.pid;
				oCurObj.FireEvent({ sName: "onAttempt" });
				var bCorrect = false;
				var iCurrentTime = (new Date()).valueOf();
				var iAttemptIdx = -1;
				for(var i=0; i<CLZ.oStore[sQuestionId].A.length; i++)
				{
					if(CLZ.oStore[sQuestionId].A[i].u=="c") continue;
					iAttemptIdx = i;
					break;
				}
				if(iAttemptIdx==-1) return false; // extra fuse
				if(CLZ.oStore[sQuestionId].R.length==0)
				{
					if(argobj.sReason=="timeout")
					{
						oCurObj.CallMethod({ sMethod: "Feedback", oMethodArgs: { correct: bCorrect } });
					}
					oCurObj.CallMethod({ sMethod: "CloseAttempt" });
					oCurObj.CallMethod({ sMethod: "CloseQuestion" });
				}
				else
				{
					var nScore = 0;
					switch(oCurObj.data.sType)
					{
						case "true-false":
						{
							switch(oCurObj.data.sScoringType)
							{
								case "plain":
								case "byobj":
								{
									if(CLZ.oStore[sQuestionId].R.length!=0)
									{
										bCorrect = (+CLZ.oStore[sQuestionId].R[0]==+CLZ.oStore[sQuestionId].P[0]);
									}
									oCurObj.CallMethod({ sMethod: "CloseAttempt", oMethodArgs: { bInitNext: false, bCorrect: bCorrect } });
									if(oCurObj.data.bFeedback)
									{
										oCurObj.CallMethod({ sMethod: "Feedback", oMethodArgs: { correct: bCorrect } });
									}
									if(bCorrect)
									{
										if(CLZ.bSoundOn && oCurObj.data.sSoundSuccess!="none")
										{
											CL.Sound.CLPlay({ jPlayer: oCurObj.data.jAudioSuccess });
										}
										oCurObj.FireEvent({ sName: "onSuccess" });
										if(oCurObj.data.bCredit)
										{
											switch(oCurObj.data.sScoringType)
											{
												case "plain":
												{
													nScore = oCurObj.data.nBaseScore;
													for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
													{
														CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
													}
													break;
												}
												case "byobj":
												{
													for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
													{
														nScore = +$(oCurObj.data.jxObjectives[i]).children("q_weight_byobj").text();
														CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
													}
													break;
												}
											}
										}
										oCurObj.CallMethod({ sMethod: "CloseQuestion" });
									}
									else
									{
										if(CLZ.bSoundOn && oCurObj.data.sSoundFailure!="none")
										{
											CL.Sound.CLPlay({ jPlayer: oCurObj.data.jAudioFailure });
										}
										oCurObj.FireEvent({ sName: "onFailure" });
										if(oCurObj.data.bShowCorrect) // display correct state
										{
											for(var i=0; i<2; i++)
											{
												if(i==+CLZ.oStore[sQuestionId].P[0])
												{
													$("#" + argobj.pid + "_Q_VAR_" + i).removeClass("cl-var-idle").addClass("cl-var-selected cl-var-disabled");
												}
												else
												{
													$("#" + argobj.pid + "_Q_VAR_" + i).removeClass("cl-var-selected").addClass("cl-var-idle cl-var-disabled");
												}
											}
											jCurObjDiv.find(".cl-q-question-goal").html(oCurObj.jxParams.children("q_goal_right").text());
										}
										if(oCurObj.data.bCredit)
										{
											nScore = 0;
											for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
											{
												CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
											}
										}
										oCurObj.CallMethod({ sMethod: "CloseQuestion" });
									}
									break;
								}
								case "byvar":
								{
									oCurObj.CallMethod({ sMethod: "CloseAttempt", oMethodArgs: { bInitNext: !bCorrect, bCorrect: true } });
									if(oCurObj.data.bCredit)
									{
										if(+CLZ.oStore[sQuestionId].R[0]==1)
										{
											nScore = +oCurObj.jxParams.children("q_weight_true").text();
										}
										else if(+CLZ.oStore[sQuestionId].R[0]==0)
										{
											nScore = +oCurObj.jxParams.children("q_weight_false").text();
										}
										for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
										{
											CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
										}
									}
									oCurObj.CallMethod({ sMethod: "CloseQuestion" });
									break;
								}
							}
							break;
						}
						case "choice":
						{
							switch(oCurObj.data.sScoringType)
							{
								case "fixed": // fixed - 2.7 legacy
								case "plain":
								case "regressive":
								case "byobj":
								{
									if(CLZ.oStore[sQuestionId].R.length!=0)
									{
										bCorrect = (+CLZ.oStore[sQuestionId].R[0]==+CLZ.oStore[sQuestionId].P[0]);
									}
									oCurObj.CallMethod({ sMethod: "CloseAttempt", oMethodArgs: { bInitNext: !bCorrect, bCorrect: bCorrect } });
									if(oCurObj.data.bFeedback)
									{
										oCurObj.CallMethod({ sMethod: "Feedback", oMethodArgs: { correct: bCorrect } });
									}
									if(bCorrect)
									{
										if(CLZ.bSoundOn && oCurObj.data.sSoundSuccess!="none")
										{
											CL.Sound.CLPlay({ jPlayer: oCurObj.data.jAudioSuccess });
										}
										oCurObj.FireEvent({ sName: "onSuccess" });
										if(oCurObj.data.bCredit)
										{
											switch(oCurObj.data.sScoringType)
											{
												case "fixed": // 2.7 legacy
												case "plain":
												{
													nScore = oCurObj.data.nBaseScore;
													for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
													{
														CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
													}
													break;
												}
												case "regressive":
												{
													nScore = oCurObj.data.nBaseScore*(CLZ.oStore[sQuestionId].A.length - iAttemptIdx);
													for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
													{
														CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
													}
													break;
												}
												case "byobj":
												{
													for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
													{
														nScore = +$(oCurObj.data.jxObjectives[i]).children("q_weight_byobj").text();
														CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
													}
													break;
												}
											}
										}
										oCurObj.CallMethod({ sMethod: "CloseQuestion" });
									}
									else
									{
										if(CLZ.bSoundOn && oCurObj.data.sSoundFailure!="none")
										{
											CL.Sound.CLPlay({ jPlayer: oCurObj.data.jAudioFailure });
										}
										oCurObj.FireEvent({ sName: "onFailure" });
										if(oCurObj.data.bNoMoreAttempts || oCurObj.data.bTimeout)
										{
											if(oCurObj.data.bShowCorrect) // display correct state
											{
												for(var i=1; i<=oCurObj.data.jxVars.length; i++)
												{
													if(i==+CLZ.oStore[sQuestionId].P[0])
													{
														$("#" + argobj.pid + "_Q_VAR_" + i).removeClass("cl-var-idle").addClass("cl-var-selected cl-var-disabled");
													}
													else
													{
														$("#" + argobj.pid + "_Q_VAR_" + i).removeClass("cl-var-selected").addClass("cl-var-idle cl-var-disabled");
													}
												}
												jCurObjDiv.find(".cl-q-question-goal").html(oCurObj.jxParams.children("q_goal_right").text());
											}
											if(oCurObj.data.bCredit)
											{
												nScore = 0;
												for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
												{
													CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
												}
											}
											oCurObj.CallMethod({ sMethod: "CloseQuestion" });
										}
									}
									break;
								}
								case "byvar":
								{
									oCurObj.CallMethod({ sMethod: "CloseAttempt", oMethodArgs: { bInitNext: false, bCorrect: true } });
									if(oCurObj.data.bCredit)
									{
										nScore = +$(oCurObj.data.jxVars[+CLZ.oStore[sQuestionId].R[0]-1]).children("q_weight_by_connection").text();
										for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
										{
											CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
										}
									}
									oCurObj.CallMethod({ sMethod: "CloseQuestion" });
									break;
								}
							}
							break;
						}
						case "select":
						{
							switch(oCurObj.data.sScoringType)
							{
								case "fixed": // 2.7 legacy
								case "plain":
								case "regressive":
								case "byobj":
								{
									if(CLZ.oStore[sQuestionId].R.length==CLZ.oStore[sQuestionId].P.length)
									{
										bCorrect = true;
										for(var i=0; i<CLZ.oStore[sQuestionId].R.length; i++)
										{
											if($.inArray(+CLZ.oStore[sQuestionId].R[i], CLZ.oStore[sQuestionId].P)==-1)
											{
												bCorrect = false;
												break;
											}
										}
									}
									oCurObj.CallMethod({ sMethod: "CloseAttempt", oMethodArgs: { bInitNext: !bCorrect, bCorrect: bCorrect } });
									if(oCurObj.data.bFeedback)
									{
										oCurObj.CallMethod({ sMethod: "Feedback", oMethodArgs: { correct: bCorrect } });
									}
									if(bCorrect)
									{
										if(CLZ.bSoundOn && oCurObj.data.sSoundSuccess!="none")
										{
											CL.Sound.CLPlay({ jPlayer: oCurObj.data.jAudioSuccess });
										}
										oCurObj.FireEvent({ sName: "onSuccess" });
										if(oCurObj.data.bCredit)
										{
											switch(oCurObj.data.sScoringType)
											{
												case "fixed": // 2.7 legacy
												case "plain":
												{
													nScore = oCurObj.data.nBaseScore;
													for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
													{
														CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
													}
													break;
												}
												case "regressive":
												{
													nScore = oCurObj.data.nBaseScore*(CLZ.oStore[sQuestionId].A.length - iAttemptIdx);
													for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
													{
														CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
													}
													break;
												}
												case "byobj":
												{
													for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
													{
														nScore = +$(oCurObj.data.jxObjectives[i]).children("q_weight_byobj").text();
														CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
													}
													break;
												}
											}
										}
										oCurObj.CallMethod({ sMethod: "CloseQuestion" });
									}
									else
									{
										if(CLZ.bSoundOn && oCurObj.data.sSoundFailure!="none")
										{
											CL.Sound.CLPlay({ jPlayer: oCurObj.data.jAudioFailure });
										}
										oCurObj.FireEvent({ sName: "onFailure" });
										if(oCurObj.data.bNoMoreAttempts || oCurObj.data.bTimeout)
										{
											if(oCurObj.data.bShowCorrect) // display correct state
											{
												for(var i=1; i<=oCurObj.data.jxVars.length; i++)
												{
													if($.inArray(i,CLZ.oStore[sQuestionId].P)!=-1)
													{
														$("#" + argobj.pid + "_Q_VAR_" + i).removeClass("cl-var-idle").addClass("cl-var-selected cl-var-disabled");
													}
													else
													{
														$("#" + argobj.pid + "_Q_VAR_" + i).removeClass("cl-var-selected").addClass("cl-var-idle cl-var-disabled");
													}
												}
												jCurObjDiv.find(".cl-q-question-goal").html(oCurObj.jxParams.children("q_goal_right").text());
											}
											if(oCurObj.data.bCredit)
											{
												nScore = 0;
												for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
												{
													CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
												}
											}
											oCurObj.CallMethod({ sMethod: "CloseQuestion" });
										}
									}
									break;
								}
								case "sum":
								case "avg":
								case "max":
								case "min":
								{
									oCurObj.CallMethod({ sMethod: "CloseAttempt", oMethodArgs: { bInitNext: false, bCorrect: true } });
									if(oCurObj.data.bCredit)
									{
										var nSum = 0;
										var nMax = -99999;
										var nMin = 99999;
										for(var i=0; i<CLZ.oStore[sQuestionId].R.length; i++)
										{
											nScore = +$(oCurObj.data.jxVars[+CLZ.oStore[sQuestionId].R[i]-1]).children("q_weight_by_connection").text();
											nSum += nScore;
											if(nMax<nScore) nMax = nScore;
											if(nMin>nScore) nMin = nScore;
										}
										switch(oCurObj.data.sScoringType)
										{
											case "sum":
											{
												nScore = nSum;
												break;
											}
											case "avg":
											{
												nScore = nSum/CLZ.oStore[sQuestionId].R.length;
												break;
											}
											case "max":
											{
												nScore = nMax;
												break;
											}
											case "min":
											{
												nScore = nMin;
												break;
											}
										}
										for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
										{
											CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
										}
									}
									oCurObj.CallMethod({ sMethod: "CloseQuestion" });
									break;
								}
							}
							break;
						}
						case "order":
						{
							switch(oCurObj.data.sScoringType)
							{
								case "fixed": // 2.7 legacy
								case "plain":
								case "regressive":
								case "byobj":
								{
									if(CLZ.oStore[sQuestionId].R.length==CLZ.oStore[sQuestionId].P.length)
									{
										bCorrect = true;
										for(var i=0; i<CLZ.oStore[sQuestionId].R.length; i++)
										{
											if(+CLZ.oStore[sQuestionId].R[i] != +CLZ.oStore[sQuestionId].P[i])
											{
												bCorrect = false;
												break;
											}
										}
									}
									oCurObj.CallMethod({ sMethod: "CloseAttempt", oMethodArgs: { bInitNext: !bCorrect, bCorrect: bCorrect } });
									if(oCurObj.data.bFeedback)
									{
										oCurObj.CallMethod({ sMethod: "Feedback", oMethodArgs: { correct: bCorrect } });
									}
									if(bCorrect)
									{
										if(CLZ.bSoundOn && oCurObj.data.sSoundSuccess!="none")
										{
											CL.Sound.CLPlay({ jPlayer: oCurObj.data.jAudioSuccess });
										}
										oCurObj.FireEvent({ sName: "onSuccess" });
										if(oCurObj.data.bCredit)
										{
											switch(oCurObj.data.sScoringType)
											{
												case "fixed": // 2.7 legacy
												case "plain":
												{
													nScore = oCurObj.data.nBaseScore;
													for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
													{
														CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
													}
													break;
												}
												case "regressive":
												{
													nScore = oCurObj.data.nBaseScore*(CLZ.oStore[sQuestionId].A.length - iAttemptIdx);
													for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
													{
														CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
													}
													break;
												}
												case "byobj":
												{
													for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
													{
														nScore = +$(oCurObj.data.jxObjectives[i]).children("q_weight_byobj").text();
														CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
													}
													break;
												}
											}
										}
										oCurObj.CallMethod({ sMethod: "CloseQuestion" });
									}
									else
									{
										if(CLZ.bSoundOn && oCurObj.data.sSoundFailure!="none")
										{
											CL.Sound.CLPlay({ jPlayer: oCurObj.data.jAudioFailure });
										}
										oCurObj.FireEvent({ sName: "onFailure" });
										if(oCurObj.data.bNoMoreAttempts || oCurObj.data.bTimeout)
										{
											if(oCurObj.data.bShowCorrect) // display correct state
											{
												var jParent = jCurObjDiv.find(".cl-q-vars");
												for(var i=1; i<=oCurObj.data.jxVars.length; i++)
												{
													$("#" + argobj.pid + "_Q_VAR_" + i).removeClass("cl-var-idle").addClass("cl-var-selected cl-var-disabled").appendTo(jParent);
												}
												jCurObjDiv.find(".cl-q-question-goal").html(oCurObj.jxParams.children("q_goal_right").text());
											}
											if(oCurObj.data.bCredit)
											{
												nScore = 0;
												for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
												{
													CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
												}
											}
											oCurObj.CallMethod({ sMethod: "CloseQuestion" });
										}
									}
									break;
								}
								case "sum":
								case "avg":
								case "max":
								case "min":
								{
									oCurObj.CallMethod({ sMethod: "CloseAttempt", oMethodArgs: { bInitNext: false, bCorrect: true } });
									if(oCurObj.data.bCredit)
									{
										var nSum = 0;
										var nMax = -99999;
										var nMin = 99999;
										for(var i=0; i<CLZ.oStore[sQuestionId].R.length; i++)
										{
											if(+CLZ.oStore[sQuestionId].R[i] != +CLZ.oStore[sQuestionId].P[i]) continue;
											nScore = +$(oCurObj.data.jxVars[+CLZ.oStore[sQuestionId].R[i]-1]).children("q_weight_by_connection").text();
											nSum += nScore;
											if(nMax<nScore) nMax = nScore;
											if(nMin>nScore) nMin = nScore;
										}
										switch(oCurObj.data.sScoringType)
										{
											case "sum":
											{
												nScore = nSum;
												break;
											}
											case "avg":
											{
												nScore = nSum/CLZ.oStore[sQuestionId].R.length;
												break;
											}
											case "max":
											{
												nScore = nMax;
												break;
											}
											case "min":
											{
												nScore = nMin;
												break;
											}
										}
										for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
										{
											CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
										}
									}
									oCurObj.CallMethod({ sMethod: "CloseQuestion" });
									break;
								}
							}
							break;
						}
						case "numeric":
						case "fib":
						{
							var aCorrect = [];
							for(var i=0; i<CLZ.oStore[sQuestionId].P.length; i++)
							{
								aCorrect.push(CL.Q.EvalSet({ sType: oCurObj.data.sType, sValue: CLZ.oStore[sQuestionId].R[i], oPattern: CLZ.oStore[sQuestionId].P[i] }));
							}
							bCorrect = (oCurObj.data.sOp=="AND");
							for(var i=0; i<aCorrect.length; i++)
							{
								if(oCurObj.data.sOp=="AND")
								{
									if(aCorrect[i]!=true)
									{
										bCorrect = false;
										break;
									}
								}
								else if(oCurObj.data.sOp=="OR")
								{
									if(aCorrect[i]==true)
									{
										bCorrect = true;
										break;
									}
								}
							}
							if(oCurObj.data.bFeedback)
							{
								oCurObj.CallMethod({ sMethod: "Feedback", oMethodArgs: { correct: bCorrect } });
							}
							oCurObj.CallMethod({ sMethod: "CloseAttempt", oMethodArgs: { bInitNext: !bCorrect, bCorrect: bCorrect } });
							if(bCorrect)
							{
								if(CLZ.bSoundOn && oCurObj.data.sSoundSuccess!="none")
								{
									CL.Sound.CLPlay({ jPlayer: oCurObj.data.jAudioSuccess });
								}
								oCurObj.FireEvent({ sName: "onSuccess" });
								if(oCurObj.data.bCredit)
								{
									switch(oCurObj.data.sScoringType)
									{
										case "fixed": // 2.7 legacy
										case "plain":
										{
											nScore = oCurObj.data.nBaseScore;
											for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
											{
												CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
											}
											break;
										}
										case "regressive":
										{
											nScore = oCurObj.data.nBaseScore*(CLZ.oStore[sQuestionId].A.length - iAttemptIdx);
											for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
											{
												CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
											}
											break;
										}
										case "byobj":
										{
											for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
											{
												nScore = +$(oCurObj.data.jxObjectives[i]).children("q_weight_byobj").text();
												CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
											}
											break;
										}
									}
								}
								oCurObj.CallMethod({ sMethod: "CloseQuestion" });
							}
							else
							{
								if(CLZ.bSoundOn && oCurObj.data.sSoundFailure!="none")
								{
									CL.Sound.CLPlay({ jPlayer: oCurObj.data.jAudioFailure });
								}
								oCurObj.FireEvent({ sName: "onFailure" });
								if(oCurObj.data.bNoMoreAttempts || oCurObj.data.bTimeout)
								{
									if(oCurObj.data.bShowCorrect) // display correct state
									{
										var jVar;
										var sString = "";
										var oPattern;
										for(var i=0; i<CLZ.oStore[sQuestionId].P.length; i++)
										{
											oPattern = CLZ.oStore[sQuestionId].P[i];
											sString = "";
											for(var j=0; j<oPattern.aConds.length; j++)
											{
												if(j>0) sString += " " + oPattern.sOp + " ";
												sString += "(";
												switch(oPattern.aConds[j].op)
												{
													case "eq": sString += "==" + oPattern.aConds[j].val; break;
													case "ne": sString += "!=" + oPattern.aConds[j].val; break;
													case "gt": sString += ">" + oPattern.aConds[j].val; break;
													case "gte": sString += ">=" + oPattern.aConds[j].val; break;
													case "lt": sString += "<" + oPattern.aConds[j].val; break;
													case "lte": sString += "<=" + oPattern.aConds[j].val; break;
													case "cn": sString += "*" + oPattern.aConds[j].val + "*"; break;
													case "ew": sString += "*" + oPattern.aConds[j].val; break;
													case "bw": sString += oPattern.aConds[j].val + "*"; break;
												}
												sString += ")";
											}
											jVar = $("#" + argobj.pid + "_Q_VAR_" + (i+1));
											jVar.find(".cl-var-input").val(sString);
											jVar.addClass("cl-var-disabled");
										}
										jCurObjDiv.find(".cl-q-question-goal").html(oCurObj.jxParams.children("q_goal_right").text());
									}
									if(oCurObj.data.bCredit)
									{
										nScore = 0;
										for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
										{
											CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
										}
									}
									oCurObj.CallMethod({ sMethod: "CloseQuestion" });
								}
							}
							break;
						}
						case "oto":
						{
							switch(oCurObj.data.sScoringType)
							{
								case "fixed": // 2.7 legacy
								case "plain":
								case "regressive":
								case "byobj":
								{
									if(CLZ.oStore[sQuestionId].R.length==CLZ.oStore[sQuestionId].P.length)
									{
										bCorrect = true;
										for(var i=0; i<CLZ.oStore[sQuestionId].R.length; i++)
										{
											if(+CLZ.oStore[sQuestionId].R[i] != +CLZ.oStore[sQuestionId].P[i])
											{
												bCorrect = false;
												break;
											}
										}
									}
									oCurObj.CallMethod({ sMethod: "CloseAttempt", oMethodArgs: { bInitNext: !bCorrect, bCorrect: bCorrect } });
									if(oCurObj.data.bFeedback)
									{
										oCurObj.CallMethod({ sMethod: "Feedback", oMethodArgs: { correct: bCorrect } });
									}
									if(bCorrect)
									{
										if(CLZ.bSoundOn && oCurObj.data.sSoundSuccess!="none")
										{
											CL.Sound.CLPlay({ jPlayer: oCurObj.data.jAudioSuccess });
										}
										oCurObj.FireEvent({ sName: "onSuccess" });
										if(oCurObj.data.bCredit)
										{
											switch(oCurObj.data.sScoringType)
											{
												case "fixed": // 2.7 legacy
												case "plain":
												{
													nScore = oCurObj.data.nBaseScore;
													for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
													{
														CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
													}
													break;
												}
												case "regressive":
												{
													nScore = oCurObj.data.nBaseScore*(CLZ.oStore[sQuestionId].A.length - iAttemptIdx);
													for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
													{
														CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
													}
													break;
												}
												case "byobj":
												{
													for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
													{
														nScore = +$(oCurObj.data.jxObjectives[i]).children("q_weight_byobj").text();
														CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
													}
													break;
												}
											}
										}
										oCurObj.CallMethod({ sMethod: "CloseQuestion" });
									}
									else
									{
										if(CLZ.bSoundOn && oCurObj.data.sSoundFailure!="none")
										{
											CL.Sound.CLPlay({ jPlayer: oCurObj.data.jAudioFailure });
										}
										oCurObj.FireEvent({ sName: "onFailure" });
										if(oCurObj.data.bNoMoreAttempts || oCurObj.data.bTimeout)
										{
											if(oCurObj.data.bShowCorrect) // display correct state
											{
												var jParent = jCurObjDiv.find(".cl-q-vars");
												for(var i=0; i<CLZ.oStore[sQuestionId].P.length; i++)
												{
													jCurObjDiv.find(".cl-var-bullet[data-vid='"+ CLZ.oStore[sQuestionId].P[i] +"']").css({ "top": "", "z-index": "" }).appendTo(oCurObj.data.jNests[i]);
												}
												jCurObjDiv.find(".cl-q-question-goal").html(oCurObj.jxParams.children("q_goal_right").text());
											}
											if(oCurObj.data.bCredit)
											{
												nScore = 0;
												for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
												{
													CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
												}
											}
											oCurObj.CallMethod({ sMethod: "CloseQuestion" });
										}
									}
									break;
								}
								case "sum":
								case "avg":
								case "max":
								case "min":
								{
									oCurObj.CallMethod({ sMethod: "CloseAttempt", oMethodArgs: { bInitNext: false, bCorrect: true } });
									if(oCurObj.data.bCredit)
									{
										var nSum = 0;
										var nMax = -99999;
										var nMin = 99999;
										for(var i=0; i<CLZ.oStore[sQuestionId].R.length; i++)
										{
											if(+CLZ.oStore[sQuestionId].R[i] != +CLZ.oStore[sQuestionId].P[i]) continue;
											nScore = +$(oCurObj.data.jxVars[+CLZ.oStore[sQuestionId].R[i]-1]).children("q_weight_by_connection").text();
											nSum += nScore;
											if(nMax<nScore) nMax = nScore;
											if(nMin>nScore) nMin = nScore;
										}
										switch(oCurObj.data.sScoringType)
										{
											case "sum":
											{
												nScore = nSum;
												break;
											}
											case "avg":
											{
												nScore = nSum/CLZ.oStore[sQuestionId].R.length;
												break;
											}
											case "max":
											{
												nScore = nMax;
												break;
											}
											case "min":
											{
												nScore = nMin;
												break;
											}
										}
										for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
										{
											CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
										}
									}
									oCurObj.CallMethod({ sMethod: "CloseQuestion" });
									break;
								}
							}
							break;
						}
					}
				}
				return true;
			}

function Feedback(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				if(oCurObj.jxParams.children("q_fb_switch").text()!="yes") return false;
				var jFB = jCurObjDiv.find("#" + argobj.pid + "_Q_FB");
				oCurObj.iFBTimer = 0;
				if(jFB.is(":visible")) jFB.fadeOut();
				if(argobj.hide!=null)
				{
					return true;
				}
				jFB.removeClass();
				var sFB = "";
				if(argobj.correct==true)
				{
					if(oCurObj.jxParams.children("q_display_fb_correct").text()=="yes")
					{
						sFB = "correct";
					}
				}
				else if(oCurObj.data.bTimeout)
				{
					if(oCurObj.jxParams.children("q_display_fb_timeout").text()=="yes")
					{
						sFB = "timeout";
					}
				}
				else if(oCurObj.data.bNoMoreAttempts)
				{
					if(oCurObj.jxParams.children("q_display_fb_exceed").text()=="yes")
					{
						sFB = "exceed";
					}
					else if(oCurObj.jxParams.children("q_display_fb_incorrect").text()=="yes")
					{
						sFB = "incorrect";
					}
				}
				else
				{
					if(CLZ.oStore[argobj.pid].A.length>1)
					{
						if(oCurObj.data.bLastAttempt)
						{
							if(oCurObj.jxParams.children("q_display_fb_lastattempt").text()=="yes")
							{
								sFB = "lastattempt";
							}
						}
						else
						{
							if(oCurObj.jxParams.children("q_display_fb_incorrect").text()=="yes")
							{
								sFB = "incorrect";
							}
						}
					}
					else
					{
						if(oCurObj.jxParams.children("q_display_fb_incorrect").text()=="yes")
						{
							sFB = "incorrect";
						}
					}
				}
				if(sFB!="")
				{
					jFB.find(".cl-q-feedback-txt").html( oCurObj.jxParams.children("q_fb_" + sFB).text() );
					jFB.addClass("cl-q-feedback cl-q-feedback-" + sFB).fadeIn();
					if(oCurObj.jxParams.children("q_fb_timerswitch").text()=="yes")
					{
						oCurObj.iFBTimer = setTimeout(function () { oCurObj.CallMethod({ sMethod: "Feedback", oMethodArgs: { hide: true } }); }, +oCurObj.jxParams.children("q_fb_timer").text());
					}
				}
				return true;
			}

function Button(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var jBtn = jCurObjDiv.find("#" + argobj.pid + "_BUTTON_" + argobj.target);
				if(jBtn.length!=0)
				{
					if(argobj.action=="show")
					{
						jBtn.show();
					}
					else if(argobj.action=="hide")
					{
						jBtn.hide();
					}
					else if(argobj.action=="remove")
					{
						jBtn.remove();
					}
				}
				return true;
			}

function Timer(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var sQuestionId = argobj.pid;
				if(!oCurObj.data.bTimer) return false;
				var jIndicator = jCurObjDiv.find(".cl-q-timer-indicator");
				var jValue = jCurObjDiv.find(".cl-q-timer .cl-q-timer-value-number");
				var iTimerLeft = CLZ.oStore[sQuestionId].t - CLZ.oStore[sQuestionId].e;
				switch(argobj.action)
				{
					case "init":
					{
						jCurObjDiv.find(".cl-q-timer").show();
						var jValueContainer = jCurObjDiv.find(".cl-q-timer .cl-q-timer-value");
						jValueContainer.css({ "width": jValueContainer.innerWidth + "px" });
						jValue.html(iTimerLeft);
						var sTW = Math.round(100 * iTimerLeft / CLZ.oStore[sQuestionId].t) + "%";
						jIndicator.width(sTW).show();
						if(oCurObj.data.sMode!="browse")
						{
							sTW = Math.round(100 * (iTimerLeft-1) / CLZ.oStore[sQuestionId].t) + "%";
							jIndicator.animate({ "width": sTW }, { duration: 1000, easing: "linear" } );
							oCurObj.data.iQTimer = setTimeout(function () { oCurObj.CallMethod({ sMethod: "Timer" }); }, 1000);
						}
						break;
					}
					case "stop":
					{
						jIndicator.stop(true, false);
						clearTimeout(oCurObj.data.iQTimer);
						oCurObj.data.iQTimer = null;
						break;
					}
					case "suspend":
					{
						break;
					}
					case "count":
					default:
					{
						if(jCurObjDiv.is(":visible"))
						{
							iTimerLeft--;
							CLZ.oStore[sQuestionId].e++;
							jValue.html(iTimerLeft);
							if(iTimerLeft>0)
							{
								var sTW = Math.round(100 * (iTimerLeft-1) / CLZ.oStore[sQuestionId].t) + "%";
								jIndicator.animate({ "width": sTW }, { duration: 1000, easing: "linear" });
								if(iTimerLeft < 0.25*CLZ.oStore[sQuestionId].t)
								{
									if(iTimerLeft < 0.125*CLZ.oStore[sQuestionId].t)
									{
										jIndicator.removeClass("cl-q-timer-indicator-warn").addClass("cl-q-timer-indicator-panic");
									}
									else
									{
										jIndicator.removeClass("cl-q-timer-indicator-idle").addClass("cl-q-timer-indicator-warn");
									}
								}
								oCurObj.data.iQTimer = setTimeout(function () { oCurObj.CallMethod({ sMethod: "Timer" }); }, 1000);
							}
							else
							{
								jIndicator.width(0).hide();
								oCurObj.data.bTimeout = true;
								oCurObj.FireEvent({ sName: "onTimeout" });
								oCurObj.CallMethod({ sMethod: "Submit", oMethodArgs: { sReason: "timeout" } });
							}
						}
						break;
					}
				}
				return true;
			}

function CloseAttempt(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var sQuestionId = argobj.pid;
				var iAttemptIdx = -1;
				for(var i=0; i<CLZ.oStore[sQuestionId].A.length; i++)
				{
					if(CLZ.oStore[sQuestionId].A[i].u=="c") continue;
					iAttemptIdx = i;
					break;
				}
				var iNow = (new Date()).valueOf();
				var iStart = CLZ.oStore[sQuestionId].A[iAttemptIdx].s;
				CLZ.oStore[sQuestionId].A[iAttemptIdx] =
				{
					R: CLZ.oStore[sQuestionId].R.slice(0),
					s: iStart,
					d: iNow - iStart - CLZ.oStore[sQuestionId].s,
					u: "c"
				}; // current attempt closed
				iAttemptIdx++;
				var iAttemptsLeft = CLZ.oStore[sQuestionId].A.length - iAttemptIdx;
				if(iAttemptsLeft<0) iAttemptsLeft = 0;
				if(argobj.bCorrect)
				{
					CLZ.oStore[sQuestionId].r = argobj.bCorrect ? "c" : "i";
				}
				jCurObjDiv.find(".cl-q-attempts-num").html( iAttemptsLeft );
				if(iAttemptsLeft==0 || CLZ.oStore[sQuestionId].A[iAttemptIdx]==null)
				{
					oCurObj.data.bNoMoreAttempts = true;
					CLO[argobj.pid].FireEvent({ sName: "onAttemptsLimit" });
				}
				else
				{
					if(argobj.bInitNext)
					{
						CLZ.oStore[sQuestionId].A[iAttemptIdx] =
						{
							s: iNow - CLZ.oStore[sQuestionId].s,
							d: 0,
							u: "i"
						}
						if((CLZ.oStore[sQuestionId].A.length>1) && (iAttemptIdx==CLZ.oStore[sQuestionId].A.length-1))
						{
							oCurObj.data.bLastAttempt = true;
							oCurObj.FireEvent({ sName: "onLastAttemptStart" });
						}
					}
				}
				return true;
			}

function CloseQuestion(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var sQuestionId = argobj.pid;
				oCurObj.CallMethod({ sMethod: "Timer", oMethodArgs: { action: "stop" } });
				if(argobj.bFail==true)
				{
					oCurObj.CallMethod({ sMethod: "Submit", oMethodArgs: { bFail: true } });
				}
				var jVars;
				switch(oCurObj.data.sType)
				{
					case "true-false":
					case "choice":
					case "select":
					{
						jVars = jCurObjDiv.find(".cl-var");
						break;
					}
					case "order":
					{
						jVars = jCurObjDiv.find(".cl-var");
						if(jVars.hasClass("ui-draggable"))
						{
							jVars.draggable("destroy");
						}
						break;
					}
					case "numeric":
					case "fib":
					{
						jVars = jCurObjDiv.find(".cl-var");
						jVars.find(".cl-var-input").prop("disabled", true);
						break;
					}
					case "oto":
					case "otm":
					case "mtm":
					{
						jVars = jCurObjDiv.find(".cl-var-bullet");
						if(jVars.hasClass("ui-draggable"))
						{
							jVars.draggable("destroy");
						}
						if(jVars.hasClass("ui-droppable"))
						{
							jVars.droppable("destroy");
						}
						break;
					}
				}
				jVars.addClass("cl-var-disabled");
				jCurObjDiv.find(".cl-btn").remove();
				if(oCurObj.data.bModal)
				{
					CL.Modal({ bOn: false, sSrcId: argobj.pid });
				}
				CLZ.oStore[sQuestionId].d = (new Date()).valueOf() - CLZ.oStore[sQuestionId].s;
				CLZ.oStore[sQuestionId].u = "c";
				oCurObj.FireEvent({ sName: "onQuestionEnd" })
				return true;
			}

function GetProperty(argobj)
			{
				if(argobj.pid==null || argobj.property==null) return "";
				if(CLO[argobj.pid]==null) return "";
				switch(argobj.property.toLowerCase())
				{
					case "questionmode":
					{
						return ((CLO[argobj.pid].data.sMode=="browse") ? "review" : "normal");
					}
					case "questiontype":
					{
						return CLO[argobj.pid].data.sType;
					}
					case "questionduration":
					{
						return ((CLO[argobj.pid].data.bTimer==true) ? CLZ.oStore[argobj.pid].t : "");
					}
					case "questiontimer":
					{
						return ((CLO[argobj.pid].data.bTimer==true) ? (CLZ.oStore[argobj.pid].t - CLZ.oStore[argobj.pid].e) : "");
					}
					case "itemquantity":
					{
						return CLO[argobj.pid].data.jxVars.length;
					}
					case "questionvalue":
					{
						var sValue = "";
						switch(CLO[argobj.pid].data.sType)
						{
							case "choice":
							case "select":
							{
								var aResult = [];
								for(var i=1; i<=CLO[argobj.pid].data.jxVars.length; i++)
								{
									aResult.push(($.inArray(i, CLZ.oStore[argobj.pid].R)==-1) ? "0" : "1");
								}
								sValue = aResult.join("~");
								break;
							}
							case "order":
							case "oto":
							{
								break;
							}
							default:
							{
								sValue = CLZ.oStore[argobj.pid].R.join("~");
								break;
							}
						}
						return sValue;
					}
					case "variantselected":
					{
						return CLZ.oStore[argobj.pid].R.sort(function (a,b) { return (a-b); }).join("~");
					}
					case "attemptslast":
					{
						var iAttemptsLeft = 0;
						for(var i=0; i<CLZ.oStore[argobj.pid].A.length; i++)
						{
							if(CLZ.oStore[sQuestionId].A[i].u=="c") continue;
							iAttemptsLeft = CLZ.oStore[argobj.pid].A.length - i;
							break;
						}
						return iAttemptsLeft;
					}
					case "attemptstotal":
					{
						return CLZ.oStore[argobj.pid].A.length;
					}
				}
				return "";
			}

function Constructor(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var bDefer = false;
				switch(oCurObj.display)
				{
					case "none":
					{
						bDefer = true;
						CL.Event.Subscribe({ aList: [ { sId: argobj.pid, sEvtName: "EVENT_OBJECT_SHOW" } ] });
						break;
					}
					case "time":
					case "frame":
					case "slide":
					{
						if(oCurObj.iBegin!=null && +oCurObj.iBegin!=0)
						{
							bDefer = true;
							CL.Event.Subscribe({ aList: [ { sId: argobj.pid, sEvtName: "EVENT_OBJECT_SHOW" } ] });
						}
						break;
					}
				}

				oCurObj.data =
				{
					bDisplayAttempts: (oCurObj.jxParams.children("display_attempts").text()=="yes"),
					bDisplayCorrects: (oCurObj.jxParams.children("display_corrects").text()=="yes"),
					bDisplayMainScore: (oCurObj.jxParams.children("display_main_score").text()=="yes"),
					bDisplayQuestion: (oCurObj.jxParams.children("display_questiontext").text()=="yes"),
					bDisplayScores: (oCurObj.jxParams.children("display_scores").text()=="yes"),
					bDisplayStats: (oCurObj.jxParams.children("display_statistics").text()=="yes"),
					bDisplayTimers: (oCurObj.jxParams.children("display_timers").text()=="yes"),
					bDisplayVariants: (oCurObj.jxParams.children("display_variants").text()=="yes"),
					sType: oCurObj.jxParams.children("display_type").text()
				};

				if(oCurObj.data.sType=="detail")
				{
					var jxObjList = oCurObj.jxParams.children("objects").children("item");
					if(jxObjList.length!=0)
					{
						var jContainer = jCurObjDiv.find(".cl-results-container");
						var jQTemplate = jCurObjDiv.find(".cl-template-storage .cl-results-q-template");
						var jTTemplate = jCurObjDiv.find(".cl-template-storage .cl-results-t-template");
						jxObjList.each(function ()
						{
							var sId = $(this).children("objid").text();
							var jxObject = CL.jxModule.find("object[id='" + sId + "']");
							var bDisplayHeader = ($(this).children("displayobjheader").text()=="yes");
							var jQBlock;
							if(jxObject.length>0)
							{
								var sType = jxObject.attr("type");
								if(sType=="q_0000" || sType=="q_0001")
								{
									if(sType=="q_0001")
									{
										var jTBlock = jTTemplate.clone(true).attr({ "data-o-id": sId, "data-o-type": sType, "data-o-toggle": "1" }).appendTo(jContainer).show();
										if(bDisplayHeader)
										{
											jTBlock.find(".cl-results-t-header .cl-results-header-title").html($(this).children("objheader").text());
										}
										else
										{
											jTBlock.find(".cl-results-t-header .cl-results-header-title").html(oCurObj.jxParams.children("text_objecttype_test").text() + " " + sId);
										}
										if(CLZ.oStore[sId]==null)
										{
											jTBlock.find(".cl-results-t-header .cl-switch").hide();
											jTBlock.find(".cl-results-t-contents").hide();
											jTBlock.find(".cl-results-t-header .cl-results-na").show();
										}
										else
										{
											jTBlock.find(".cl-results-t-header .cl-switch").show();
											jTBlock.find(".cl-results-t-contents").show();
											jTBlock.find(".cl-results-t-header .cl-results-na").hide();
										}
									}
									else
									{
										jParent = jContainer;
										jQBlock = jQTemplate.clone(true).attr({ "data-o-id": sId, "data-o-type": sType, "data-o-toggle": "1"  }).appendTo(jParent).show();
										if(bDisplayHeader)
										{
											jQBlock.find(".cl-results-header .cl-results-header-title").html($(this).children("objheader").text());
										}
										else
										{
											jQBlock.find(".cl-results-header .cl-results-header-title").html(oCurObj.jxParams.children("text_objecttype_question").text() + " " + sId);
										}
										if(CLZ.oStore[sId]==null)
										{
											jQBlock.find(".cl-switch").hide();
											jQBlock.find(".cl-results-contents").hide();
											jQBlock.find(".cl-results-na").show();
											jQBlock.find(".cl-results-header-score").hide();
										}
										else
										{
											if(CLZ.oStore[sId].u=="i")
											{
												jQBlock.find(".cl-switch").hide();
												jQBlock.find(".cl-results-contents").hide();
												jQBlock.find(".cl-results-na").show();
												jQBlock.find(".cl-results-header-score").hide();
											}
											else if(CLZ.oStore[sId].u=="c")
											{
												jQBlock.find(".cl-switch").show();
												jQBlock.find(".cl-results-contents").show();
												jQBlock.find(".cl-results-na").hide();
												jQBlock.find(".cl-results-header-score").show();
											}
										}
									}
								}
							}
						});
						jContainer.find("div.cl-switch").css({ "cursor": "pointer" }).on("click", function (e) { oCurObj.CallMethod({ sMethod: "Toggle", oMethodArgs: { oElem: this, oEvt: e } }) });
					}
				}

				oCurObj.data.bCreated = true;
				oCurObj.FireEvent({ sName: "onCreated" });
				if(!bDefer)
				{
					oCurObj.FireEvent({ sName: "onStartDisplay" });
					oCurObj.CallMethod({ sMethod: "Display" });
				}
				else
				{
					jCurObjDiv.find(".container:first").hide();
				}
				return true;
			}

function Toggle(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				if(oCurObj==null || jCurObjDiv==null || argobj.oElem==null) return false;
				var jParent = $(argobj.oElem).parents(".cl-results-parent:first");
				var jContent = jParent.children(".cl-results-contents");
				if(jContent.attr("data-in-trans")!=null) return false;
				if(jContent.is(":visible"))
				{
					jContent.attr({ "data-in-trans": "1" }).slideUp(400, function () { this.removeAttribute("data-in-trans") });
					$(argobj.oElem).find(".cl-switch-path-minus").hide();
					$(argobj.oElem).find(".cl-switch-path-plus").show();
				}
				else
				{
					jContent.attr({ "data-in-trans": "1" }).slideDown(400, function () { this.removeAttribute("data-in-trans") });
					$(argobj.oElem).find(".cl-switch-path-plus").hide();
					$(argobj.oElem).find(".cl-switch-path-minus").show();
				}
				return true;
			}

function HandleEvt(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				if(oCurObj==null || jCurObjDiv==null) return false;
				switch(argobj.evt)
				{
					case "EVENT_OBJECT_SHOW":
					{
						if(!oCurObj.data.bCreated) return false;
						oCurObj.FireEvent({ sName: "onStartDisplay" });
						oCurObj.CallMethod({ sMethod: "Display" });
						break;
					}
				}
				return true;
			}

function Display(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				oCurObj.CallMethod({ sMethod: "Refresh" });
				jCurObjDiv.find(".container:first").show();
				return true;
			}

function Refresh(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				switch(oCurObj.data.sType)
				{
					case "plain":
					{
						var sObjId = oCurObj.jxParams.children("q_board").text();
						if(sObjId=="") return false;
						var nScore = +CLJ[sObjId].nRawScore;
						var nMaxScore = +CLJ[sObjId].nMax;
						var sPlainType = oCurObj.jxParams.children("plain_values").text();
						var sD1 = "";
						var sD2 = "";
						var nPercent = (nMaxScore==0) ? 0 : Math.round(100*nScore/nMaxScore);
						switch(sPlainType)
						{
							case "score":
							{
								sD1 = nScore.toString();
								sD2 = nMaxScore.toString();
								break;
							}
							case "percent":
							{
								sD1 = nPercent + "%";
								sD2 = "100%";
								break;
							}
							case "score_percent":
							{
								sD1 = nScore.toString() + " (" + nPercent + "%)";
								sD2 = nMaxScore.toString() + " (100%)";
								break;
							}
						}
						var sString = oCurObj.jxParams.children("plain_text").text();
						var aParts = sString.split("{{1}}");
						sString = aParts.join(sD1);
						aParts = sString.split("{{2}}");
						jCurObjDiv.find(".cl-table-plain-td").html(aParts.join(sD2));
						break;
					}
					case "scale":
					{
						var jxObjs = oCurObj.jxParams.find("scoreboards > item");
						if(jxObjs.length==0) return false;
						var jxItem;
						var nScore;
						var nMaxScore;
						var sScaleType = oCurObj.jxParams.children("scale_values").text();
						var sObjId;
						var sD1 = "";
						var sD2 = "";
						var nPercent;
						var sString = oCurObj.jxParams.children("scale_text").text();
						var sTmpString;
						for(var i=1; i<=jxObjs.length; i++)
						{
							jxItem = $(jxObjs[i-1]);
							sObjId = jxItem.children("scoreboard").text();
							if(sObjId=="") continue;
							nScore = +CLJ[sObjId].nRawScore;
							nMaxScore = +CLJ[sObjId].nMax;
							nPercent = (nMaxScore==0) ? 0 : Math.round(100*nScore/nMaxScore);
							switch(sScaleType)
							{
								case "score":
								{
									sD1 = nScore.toString();
									sD2 = nMaxScore.toString();
									break;
								}
								case "percent":
								{
									sD1 = nPercent + "%";
									sD2 = "100%";
									break;
								}
								case "score_percent":
								{
									sD1 = nScore.toString() + " (" + nPercent + "%)";
									sD2 = nMaxScore.toString() + " (100%)";
									break;
								}
							}
							sTmpString = sString;
							var aParts = sTmpString.split("{{1}}");
							sTmpString = aParts.join(sD1);
							aParts = sTmpString.split("{{2}}");
							jCurObjDiv.find(".cl-table-scale-h-value-" + i).html(aParts.join(sD2));
							jCurObjDiv.find(".cl-table-scale-h-scale-" + i + " .cl-table-scale-h-ind").css({ "margin-left": "-" + (100-nPercent) + "%" });
						}
						break;
					}
					case "detail":
					{
						function fnGetText(oArgs)
						{
							var jDiv = $(document.createElement("div")).html(oArgs.sHTML);
							var sTxt = jDiv.text();
							jDiv.remove();
							return sTxt;
						}
						function fnFillVars(oArgs)
						{
							// jVarTable, jxParams, sId, oStore
							var jRow;
							var jCell;
							switch(oArgs.sType)
							{
								case "true-false":
								case "choice":
								case "select":
								{
									jRow = $(document.createElement("tr")).appendTo(oArgs.jVarTable);
									$(document.createElement("td")).addClass("cl-results-q-variants-header").attr({ "colspan": "2" }).html(oCurObj.jxParams.children("text_constant_youranswer").text()).appendTo(jRow);
									if(oCurObj.data.bDisplayCorrects)
									{
										$(document.createElement("td")).addClass("cl-results-q-variants-header").html(oCurObj.jxParams.children("text_constant_correct").text()).appendTo(jRow);
									}
									break;
								}
								case "order":
								case "numeric":
								case "fib":
								{
									jRow = $(document.createElement("tr")).appendTo(oArgs.jVarTable);
									$(document.createElement("td")).addClass("cl-results-q-variants-header").html(oCurObj.jxParams.children("text_constant_youranswer").text()).appendTo(jRow);
									if(oCurObj.data.bDisplayCorrects)
									{
										$(document.createElement("td")).addClass("cl-results-q-variants-header").html(oCurObj.jxParams.children("text_constant_correct").text()).appendTo(jRow);
									}
									break;
								}
								case "oto":
								{
									jRow = $(document.createElement("tr")).appendTo(oArgs.jVarTable);
									$(document.createElement("td")).addClass("cl-results-q-variants-header").html(oCurObj.jxParams.children("text_constant_targets").text()).appendTo(jRow);
									$(document.createElement("td")).addClass("cl-results-q-variants-header").html(oCurObj.jxParams.children("text_constant_bullets").text()).appendTo(jRow);
									if(oCurObj.data.bDisplayCorrects)
									{
										$(document.createElement("td")).addClass("cl-results-q-variants-header").html(oCurObj.jxParams.children("text_constant_correct").text()).appendTo(jRow);
									}
									break;
								}
							}
							switch(oArgs.sType)
							{
								case "true-false":
								{
									jRow = $(document.createElement("tr")).attr({ "data-var-id": "true" }).appendTo(oArgs.jVarTable);
									jCell = $(document.createElement("td")).addClass("cl-results-q-variants-mark").html("&nbsp;").appendTo(jRow);
									if(oArgs.oStore.R!=null)
									{
										if(oArgs.oStore.R[0]!=null)
										{
											if(+oArgs.oStore.R[0]==1)
											{
												jCell.addClass("cl-results-mark-selected");
												if(oCurObj.data.bDisplayCorrects)
												{
													if(+oArgs.oStore.P[0]==1)
													{
														jCell.addClass("cl-results-mark-correct cl-results-correct").html("&#10003;");
													}
													else
													{
														jCell.addClass("cl-results-mark-incorrect cl-results-incorrect").html("&#10007;")
													}
												}
												else
												{
													jCell.html("&#10003;");
												}
											}
										}
									}
									jCell = $(document.createElement("td")).addClass("cl-results-q-variants-text").html(fnGetText({ sHTML: oArgs.jxParams.children("q_text_true").text() })).appendTo(jRow);
									if(oArgs.oStore.R!=null)
									{
										if(oArgs.oStore.R[0]!=null)
										{
											if(+oArgs.oStore.R[0]==1)
											{
												jCell.addClass("cl-results-var-selected");
												if(oCurObj.data.bDisplayCorrects)
												{
													if(+oArgs.oStore.P[0]==1)
													{
														jCell.addClass("cl-results-correct");
													}
													else
													{
														jCell.addClass("cl-results-incorrect");
													}
												}
											}
										}
									}
									if(oCurObj.data.bDisplayCorrects)
									{
										jCell = $(document.createElement("td")).addClass("cl-results-q-variants-mark").html("&nbsp;").appendTo(jRow);
										if(+oArgs.oStore.P[0]==1)
										{
											jCell.addClass("cl-results-mark-correct cl-results-correct").html("&#10003;");
										}
									}
									jRow = $(document.createElement("tr")).attr({ "data-var-id": "false" }).appendTo(oArgs.jVarTable);
									jCell = $(document.createElement("td")).addClass("cl-results-q-variants-mark").html("&nbsp;").appendTo(jRow);
									if(oArgs.oStore.R!=null)
									{
										if(oArgs.oStore.R[0]!=null)
										{
											if(+oArgs.oStore.R[0]!=1)
											{
												jCell.addClass("cl-results-mark-selected");
												if(oCurObj.data.bDisplayCorrects)
												{
													if(+oArgs.oStore.P[0]!=1)
													{
														jCell.addClass("cl-results-mark-correct cl-results-correct").html("&#10003;");
													}
													else
													{
														jCell.addClass("cl-results-mark-incorrect cl-results-incorrect").html("&#10007;")
													}
												}
												else
												{
													jCell.html("&#10003;");
												}
											}
										}
									}
									jCell = $(document.createElement("td")).addClass("cl-results-q-variants-text").text(fnGetText({ sHTML: oArgs.jxParams.children("q_text_false").text() })).appendTo(jRow);
									if(oArgs.oStore.R!=null)
									{
										if(oArgs.oStore.R[0]!=null)
										{
											if(+oArgs.oStore.R[0]!=1)
											{
												jCell.addClass("cl-results-var-selected");
												if(oCurObj.data.bDisplayCorrects)
												{
													if(+oArgs.oStore.P[0]!=1)
													{
														jCell.addClass("cl-results-correct");
													}
													else
													{
														jCell.addClass("cl-results-incorrect");
													}
												}
											}
										}
									}
									if(oCurObj.data.bDisplayCorrects)
									{
										jCell = $(document.createElement("td")).addClass("cl-results-q-variants-mark").html("&nbsp;").appendTo(jRow);
										if(+oArgs.oStore.P[0]!=1)
										{
											jCell.addClass("cl-results-mark-correct cl-results-correct").html("&#10003;");
										}
									}
									break;
								}
								case "choice":
								{
									var jxVars = oArgs.jxParams.find("q_variants_choice:first > item");
									for(var k=1; k<=jxVars.length; k++)
									{
										jRow = $(document.createElement("tr")).appendTo(oArgs.jVarTable);
										jCell = $(document.createElement("td")).addClass("cl-results-q-variants-mark").html("&nbsp;").appendTo(jRow);
										if(oArgs.oStore.R!=null)
										{
											if(oArgs.oStore.R[0]!=null)
											{
												if(+oArgs.oStore.R[0]==k)
												{
													jCell.addClass("cl-results-mark-selected");
													if(oCurObj.data.bDisplayCorrects)
													{
														if(+oArgs.oStore.P[0]==k)
														{
															jCell.addClass("cl-results-mark-correct cl-results-correct").html("&#10003;");
														}
														else
														{
															jCell.addClass("cl-results-mark-incorrect cl-results-incorrect").html("&#10007;")
														}
													}
													else
													{
														jCell.html("&#10003;");
													}
												}
											}
										}
										jCell = $(document.createElement("td")).addClass("cl-results-q-variants-text").attr({ "data-var-id": k }).html(fnGetText({ sHTML: $(jxVars[k-1]).children("q_variant").text() })).appendTo(jRow);
										if(oArgs.oStore.R!=null)
										{
											if(oArgs.oStore.R[0]!=null)
											{
												if(+oArgs.oStore.R[0]==k)
												{
													jCell.addClass("cl-results-var-selected");
													if(oCurObj.data.bDisplayCorrects)
													{
														if(+oArgs.oStore.P[0]==k)
														{
															jCell.addClass("cl-results-correct");
														}
														else
														{
															jCell.addClass("cl-results-incorrect");
														}
													}
												}
											}
										}
										if(oCurObj.data.bDisplayCorrects)
										{
											jCell = $(document.createElement("td")).addClass("cl-results-q-variants-mark").html("&nbsp;").appendTo(jRow);
											if(+oArgs.oStore.P[0]==k)
											{
												jCell.addClass("cl-results-mark-correct cl-results-correct").html("&#10003;");
											}
										}
									}
									break;
								}
								case "select":
								{
									var jxVars = oArgs.jxParams.find("q_variants_select:first > item");
									for(var k=1; k<=jxVars.length; k++)
									{
										jRow = $(document.createElement("tr")).appendTo(oArgs.jVarTable);
										jCell = $(document.createElement("td")).addClass("cl-results-q-variants-mark").html("&nbsp;").appendTo(jRow);
										if(oArgs.oStore.R!=null)
										{
											if($.inArray(k, oArgs.oStore.R)!=-1)
											{
												jCell.addClass("cl-results-mark-selected");
												if(oCurObj.data.bDisplayCorrects)
												{
													if($.inArray(k, oArgs.oStore.P)!=-1)
													{
														jCell.addClass("cl-results-mark-correct cl-results-correct").html("&#10003;");
													}
													else
													{
														jCell.addClass("cl-results-mark-incorrect cl-results-incorrect").html("&#10007;")
													}
												}
												else
												{
													jCell.html("&#10003;");
												}
											}
										}
										jCell = $(document.createElement("td")).addClass("cl-results-q-variants-text").html(fnGetText({ sHTML: $(jxVars[k-1]).children("q_variant").text() })).appendTo(jRow);
										if(oArgs.oStore.R!=null)
										{
											if($.inArray(k, oArgs.oStore.R)!=-1)
											{
												jCell.addClass("cl-results-var-selected");
												if(oCurObj.data.bDisplayCorrects)
												{
													if($.inArray(k, oArgs.oStore.P)!=-1)
													{
														jCell.addClass("cl-results-correct");
													}
													else
													{
														jCell.addClass("cl-results-incorrect");
													}
												}
											}
										}
										if(oCurObj.data.bDisplayCorrects)
										{
											jCell = $(document.createElement("td")).addClass("cl-results-q-variants-mark").html("&nbsp;").appendTo(jRow);
											if($.inArray(k, oArgs.oStore.P)!=-1)
											{
												jCell.addClass("cl-results-mark-correct cl-results-correct").html("&#10003;");
											}
										}
									}
									break;
								}
								case "order":
								{
									var jxVars = oArgs.jxParams.find("q_variants_order:first > item");
									var iIdx = -1;
									if(oArgs.oStore.R!=null && oArgs.oStore.R.length==jxVars.length)
									{
										for(var k=1; k<=jxVars.length; k++)
										{
											iIdx = +oArgs.oStore.R[k-1];
											jRow = $(document.createElement("tr")).appendTo(oArgs.jVarTable);
											jCell = $(document.createElement("td")).addClass("cl-results-q-variants-text").html(fnGetText({ sHTML: $(jxVars[iIdx-1]).children("q_variant").text() })).appendTo(jRow);
											if(oCurObj.data.bDisplayCorrects)
											{
												if(iIdx==k)
												{
													jCell.addClass("cl-results-correct");
												}
												else
												{
													jCell.addClass("cl-results-incorrect");
												}
												jCell = $(document.createElement("td")).addClass("cl-results-q-variants-text").html(fnGetText({ sHTML: $(jxVars[k-1]).children("q_variant").text() })).appendTo(jRow);
											}
										}
									}
									break;
								}
								case "numeric":
								case "fib":
								{
									var jxVars = oArgs.jxParams.find("q_variants_" + oArgs.sType + ":first > item");
									var jxCond;
									var sOp;
									var aCond;
									var oSigns = { "eq": "=", "ne": "!=", "lt": "&lt;", "lte": "&lt;=", "gt": "&gt;", "gte": "&gt;=" };
									if(oArgs.oStore.R!=null && oArgs.oStore.R.length!=0)
									{
										for(var k=1; k<=jxVars.length; k++)
										{
											jRow = $(document.createElement("tr")).appendTo(oArgs.jVarTable);
											jCell = $(document.createElement("td")).addClass("cl-results-q-variants-text").attr({ "data-var-id": k }).html(oArgs.oStore.R[k-1]).appendTo(jRow);
											if(oCurObj.data.bDisplayCorrects)
											{
												if(CL.Q.EvalSet({ sType: oArgs.sType, sValue: oArgs.oStore.R[k-1], oPattern: oArgs.oStore.P[k-1] })==true)
												{
													jCell.addClass("cl-results-correct");
												}
												else
												{
													jCell.addClass("cl-results-incorrect");
												}
												aCond = [];
												sOp = $(jxVars[k-1]).children("q_cond_logic").text();
												jxCond = $(jxVars[k-1]).find("q_conditions > item");
												if(oArgs.sType=="numeric")
												{
													for(var j=0; j<jxCond.length; j++)
													{
														aCond.push( oSigns[$(jxCond[j]).children("q_condition").text()] + $(jxCond[j]).children("q_variant").text() );
													}
												}
												else
												{
													for(var j=0; j<jxCond.length; j++)
													{
														switch($(jxCond[j]).children("q_condition").text())
														{
															case "eq":
															{
																aCond.push( "=" + $(jxCond[j]).children("q_variant").text() );
																break;
															}
															case "bw":
															{
																aCond.push( $(jxCond[j]).children("q_variant").text() + "*");
																break;
															}
															case "eq":
															{
																aCond.push( "*" + $(jxCond[j]).children("q_variant").text() );
																break;
															}
															case "eq":
															{
																aCond.push( "*" + $(jxCond[j]).children("q_variant").text() + "*" );
																break;
															}
														}
													}
												}
												$(document.createElement("td")).addClass("cl-results-q-variants-text").html( aCond.join(" " + sOp + " ") ).appendTo(jRow);
											}
										}
									}
									break;
								}
								case "oto":
								{
									var jxVars = oArgs.jxParams.find("q_variants_oto:first > item");
									if(oArgs.oStore.R!=null && oArgs.oStore.R.length!=0)
									{
										for(var k=1; k<=jxVars.length; k++)
										{
											jRow = $(document.createElement("tr")).attr({ "data-var-id": k }).appendTo(oArgs.jVarTable);
											jCell = $(document.createElement("td")).addClass("cl-results-q-variants-text").attr({ "data-var-id": k }).html(fnGetText({ sHTML: $(jxVars[k-1]).children("q_target").text() })).appendTo(jRow);
											if(oCurObj.data.bDisplayCorrects)
											{
												if(+oArgs.oStore.R[k-1]==k)
												{
													jCell.addClass("cl-results-correct");
												}
												else
												{
													jCell.addClass("cl-results-incorrect");
												}
											}
											if(oArgs.oStore.R[k-1]!="" && !isNaN(+oArgs.oStore.R[k-1]))
											{
												jCell = $(document.createElement("td")).addClass("cl-results-q-variants-text").attr({ "data-var-id": k }).html(fnGetText({ sHTML: $(jxVars[+oArgs.oStore.R[k-1]-1]).children("q_bullet").text() })).appendTo(jRow);
												if(oCurObj.data.bDisplayCorrects)
												{
													if(+oArgs.oStore.R[k-1]==k)
													{
														jCell.addClass("cl-results-correct");
													}
													else
													{
														jCell.addClass("cl-results-incorrect");
													}
												}
											}
											else
											{
												jCell = $(document.createElement("td")).addClass("cl-results-q-variants-text").attr({ "data-var-id": k }).html("&nbsp;").appendTo(jRow);
											}
											if(oCurObj.data.bDisplayCorrects)
											{
												jCell = $(document.createElement("td")).addClass("cl-results-q-variants-text").attr({ "data-var-id": k }).html(fnGetText({ sHTML: $(jxVars[k-1]).children("q_bullet").text() })).appendTo(jRow);
											}
										}
									}
									break;
								}
							}
						}

						var jxObjList = oCurObj.jxParams.children("objects").children("item");
						if(jxObjList.length!=0 && oCurObj.data.bCreated)
						{
							var jContainer = jCurObjDiv.find(".cl-results-container");
							var jQTemplate = jCurObjDiv.find(".cl-template-storage .cl-results-q-template");
							var jTTemplate = jCurObjDiv.find(".cl-template-storage .cl-results-t-template");
							var jQBlock;
							var jTBlock;
							var sMainObj = "";
							if(oCurObj.data.bDisplayMainScore)
							{
								for(var sKey in CLJ)
								{
									if(+CLJ[sKey].bModule==1)
									{
										sMainObj = sKey;
										break;
									}
								}
							}
							var jTxtDiv = $(document.createElement("div"));
							jxObjList.each(function ()
							{
								var sId = $(this).children("objid").text();
								var jxObject = CL.jxModule.find("object[id='" + sId + "']");
								var jxParams = CL.jxParams.children("param[objectid='" + sId + "']");
								var bDisplayHeader = ($(this).children("displayobjheader").text()=="yes");
								var jxObjectives;
								var sTxt;
								if(jxObject.length>0)
								{
									var sType = jxObject.attr("type");
									if(sType=="q_0000" || sType=="q_0001")
									{
										if(sType=="q_0001")
										{
											if(CLZ.oStore[sId]!=null)
											{
												var jTBlock = jContainer.find("div[data-o-id='" + sId + "']");
												var jContents = jTBlock.children(".cl-results-t-contents").html("");
												var jxTestParams = CL.jxParams.children("param[objectid='" + sId + "']");
												var jxQuestions = jxTestParams.find("q_questions:first > item");
												var bRandom = (jxTestParams.find("randomize").text()=="yes");
												var iToSelect = +jxTestParams.find("random_quantity").text();
												var iQuestionsInTest = (bRandom ? (iToSelect>jxQuestions.length ? jxQuestions.length : iToSelect) : jxQuestions.length);
												var jxQuestion;
												var sQId;
												var jxQParams;
												var iIdx;
												var sType;
												var oOldType;
												var oScoring;
												var sScoring;
												var iAttemptsLeft;
												var sObjId;
												var jRow;
												var jCell;
												var jScoreTable;
												var jVarTable;
												for(var i=0; i<iQuestionsInTest; i++)
												{
													iIdx = (bRandom ? CLZ.oStore[sId].P[i] : i);
													sQId = sId + "_" + iIdx;
													jQBlock = jQTemplate.clone(true).addClass("cl-results-q").attr({ "data-o-id": sId, "data-o-type": sType, "data-q-id": iIdx, "data-o-toggle": "1" }).appendTo(jContents).show();
													if(bDisplayHeader)
													{
														jQBlock.find(".cl-results-header .cl-results-header-title").html($(this).children("objheader").text() + " (" + (i+1) + "/" + iQuestionsInTest + ")");
													}
													else
													{
														jQBlock.find(".cl-results-header .cl-results-header-title").html(oCurObj.jxParams.children("text_objecttype_test").text() + " " + sId + " (" + (i+1) + "/" + iQuestionsInTest + ")");
													}
													if(CLZ.oStore[sId].Q[i]!=null)
													{
														jxQParams = $(jxQuestions[iIdx]);
														switch(CLZ.oStore[sId].Q[i].u)
														{
															case "c":
															{
																jQBlock.find(".cl-switch").show().on("click", function (e) { oCurObj.CallMethod({ sMethod: "Toggle", oMethodArgs: { oElem: this, oEvt: e } }) });
																jQBlock.find(".cl-results-contents").show();
																jQBlock.find(".cl-results-na").hide();
																jQBlock.find(".cl-results-header-score").show();
																sType = jxQParams.children("question_type").text();
																if(sMainObj!="")
																{
																	if(CLJ[sMainObj]!=null)
																	{
																		if(oCurObj.data.bDisplayMainScore && CLJ[sMainObj].oSrc[sQId]!=null)
																		{
																			jQBlock.find(".cl-results-header-score-div").html(CLJ[sMainObj].oSrc[sQId].nRawScore);
																		}
																	}
																}
																if(oCurObj.data.bDisplayQuestion)
																{
																	jQBlock.find(".cl-results-q-text").html(fnGetText({ sHTML: jxQParams.children("text_main").text() }));
																}
																if(oCurObj.data.bDisplayStats)
																{
																	oOldType = { "true-false": "tf", "choice": "choice", "select": "select", "order": "range", "numeric": "numeric", "fib": "text", "oto": "oto", "otm": "otm", "mtm": "mtm" };
																	oScoring = { "true-false": "tf", "choice": "choice", "select": "choice", "order": "choice", "numeric": "choice", "fib": "choice", "oto": "choice", "otm": "choice", "mtm": "choice" };
																	jQBlock.find(".cl-stats-q-type").html(oCurObj.jxParams.children("text_objecttype_" + oOldType[sType]).text());
																	sScoring = jxQParams.children("q_scoring_type_" + oScoring[sType]).text();
																	jQBlock.find(".cl-stats-q-scoring").html(oCurObj.jxParams.children("text_constant_scoringtype").text() + " " + oCurObj.jxParams.children("text_scoringtype_" + sScoring).text());
																	if(oCurObj.data.bDisplayAttempts)
																	{
																		iAttemptsLeft = 0;
																		for(var j=0; j<CLZ.oStore[sId].Q[i].A.length; j++)
																		{
																			if(CLZ.oStore[sId].Q[i].A[j].u=="c") continue;
																			iAttemptsLeft = CLZ.oStore[sId].Q[i].A.length - j;
																			break;
																		}
																		jQBlock.find(".cl-stats-q-attempts").html(oCurObj.jxParams.children("text_constant_attemptsmax").text() + " " + CLZ.oStore[sId].Q[i].A.length + "<br/>" + oCurObj.jxParams.children("text_constant_attemptsleft").text() + " " + iAttemptsLeft);
																	}
																	if(oCurObj.data.bDisplayTimers && CLZ.oStore[sId].Q[i].t!=null)
																	{
																		jQBlock.find(".cl-stats-q-timing").html(oCurObj.jxParams.children("text_constant_duration").text() + " " + CLZ.oStore[sId].Q[i].t + "<br/>" + oCurObj.jxParams.children("text_constant_latency").text() + " " + CLZ.oStore[sId].Q[i].e);
																	}
																	else
																	{
																		jQBlock.find(".cl-stats-q-timing").hide();
																	}
																	if(oCurObj.data.bDisplayScores)
																	{
																		jxObjectives = jxQParams.find("q_scoreboards > item");
																		jScoreTable = jQBlock.find(".cl-stats-obj-table");
																		if(jScoreTable.children("tbody").length!=0)
																		{
																			jScoreTable = jScoreTable.children("tbody:first");
																		}
																		for(var j=0; j<jxObjectives.length; j++)
																		{
																			sObjId = $(jxObjectives[j]).children("q_board").text();
																			jRow = jScoreTable.children("tr[data-obj-id='" + sObjId + "']");
																			if(jRow.length==0)
																			{
																				jRow = $(document.createElement("tr")).attr({ "data-obj-id": sObjId }).appendTo(jScoreTable);
																				$(document.createElement("td")).addClass("cl-stats-obj-obj").attr({ "data-obj-id": sObjId }).html(CLJ[sObjId].sName).appendTo(jRow);
																				$(document.createElement("td")).addClass("cl-stats-obj-score").attr({ "data-obj-id": sObjId }).appendTo(jRow);
																			}
																			if(CLJ[sObjId].oSrc[sQId]!=null)
																			{
																				jRow.children(".cl-stats-obj-score").html(CLJ[sObjId].oSrc[sQId].nRawScore);
																			}
																			else
																			{
																				jRow.children(".cl-stats-obj-score").html("0");
																			}
																		}
																	}
																	else
																	{
																		jQBlock.find(".cl-stats-right").hide();
																	}
																}
																if(oCurObj.data.bDisplayVariants)
																{
																	jVarTable = jQBlock.find(".cl-results-q-variants-table");
																	if(jVarTable.length!=0)
																	{
																		jVarTable.remove();
																	}
																	jVarTable = $(document.createElement("table")).addClass("cl-results-q-variants-table").appendTo(jQBlock.find(".cl-results-variants .cl-results-contents"));
																	jVarTable = $(document.createElement("tbody")).appendTo(jVarTable);
																	fnFillVars({ sId: sQId, sType: sType, jVarTable: jVarTable, jxParams: jxQParams, oStore: CLZ.oStore[sId].Q[i] });
																}
																break;
															}
															case "i":
															{
																jQBlock.find(".cl-switch").hide();
																jQBlock.find(".cl-results-contents").hide();
																jQBlock.find(".cl-results-na").show();
																jQBlock.find(".cl-results-header-score").hide();
																break;
															}
														}
													}
													else
													{
														jQBlock.find(".cl-switch").hide();
														jQBlock.find(".cl-results-contents").hide();
														jQBlock.find(".cl-results-na").show();
														jQBlock.find(".cl-results-header-score").hide();
													}
												}
											}
										}
										else
										{
											jQBlock = jContainer.find("div[data-o-id='" + sId + "']");
											if(CLZ.oStore[sId]!=null)
											{
												switch(CLZ.oStore[sId].u)
												{
													case "c":
													{
														var sType = jxParams.children("question_type").text();
														if(sMainObj!="")
														{
															if(CLJ[sMainObj]!=null)
															{
																if(oCurObj.data.bDisplayMainScore && CLJ[sMainObj].oSrc[sId]!=null)
																{
																	jQBlock.find(".cl-results-header-score-div").html(CLJ[sMainObj].oSrc[sId].nRawScore);
																}
															}
														}
														if(oCurObj.data.bDisplayQuestion)
														{
															jTxtDiv.html(jxParams.children("text_main").text());
															jQBlock.find(".cl-results-q-text").html(jTxtDiv.text());
														}
														if(oCurObj.data.bDisplayStats)
														{
															var oOldType = { "true-false": "tf", "choice": "choice", "select": "select", "order": "range", "numeric": "numeric", "fib": "text", "oto": "oto", "otm": "otm", "mtm": "mtm" };
															var oScoring = { "true-false": "tf", "choice": "choice", "select": "select", "order": "match", "numeric": "inputs", "fib": "inputs", "oto": "match", "otm": "match", "mtm": "match" };
															jQBlock.find(".cl-stats-q-type").html(oCurObj.jxParams.children("text_objecttype_" + oOldType[sType]).text());
															var sScoring = jxParams.children("q_scoring_type_" + oScoring[sType]).text();
															jQBlock.find(".cl-stats-q-scoring").html(oCurObj.jxParams.children("text_constant_scoringtype").text() + " " + oCurObj.jxParams.children("text_scoringtype_" + sScoring).text());
															if(oCurObj.data.bDisplayAttempts)
															{
																var iAttemptsLeft = 0;
																for(var i=0; i<CLZ.oStore[sId].A.length; i++)
																{
																	if(CLZ.oStore[sId].A[i].u=="c") continue;
																	iAttemptsLeft = CLZ.oStore[sId].A.length - i;
																	break;
																}
																jQBlock.find(".cl-stats-q-attempts").html(oCurObj.jxParams.children("text_constant_attemptsmax").text() + " " + CLZ.oStore[sId].A.length + "<br/>" + oCurObj.jxParams.children("text_constant_attemptsleft").text() + " " + iAttemptsLeft);
															}
															if(oCurObj.data.bDisplayTimers && CLZ.oStore[sId].t!=null)
															{
																jQBlock.find(".cl-stats-q-timing").html(oCurObj.jxParams.children("text_constant_duration").text() + " " + CLZ.oStore[sId].t + "<br/>" + oCurObj.jxParams.children("text_constant_latency").text() + " " + CLZ.oStore[sId].e);
															}
															else
															{
																jQBlock.find(".cl-stats-q-timing").hide();
															}
															if(oCurObj.data.bDisplayScores)
															{
																jxObjectives = jxParams.find("q_scoreboards > item");
																var sObjId;
																var jRow;
																var jCell;
																var jScoreTable = jQBlock.find(".cl-stats-obj-table");
																if(jScoreTable.children("tbody").length!=0)
																{
																	jScoreTable = jScoreTable.children("tbody:first");
																}
																for(var i=0; i<jxObjectives.length; i++)
																{
																	sObjId = $(jxObjectives[i]).children("q_board").text();
																	jRow = jScoreTable.children("tr[data-obj-id='" + sObjId + "']");
																	if(jRow.length==0)
																	{
																		jRow = $(document.createElement("tr")).attr({ "data-obj-id": sObjId }).appendTo(jScoreTable);
																		$(document.createElement("td")).addClass("cl-stats-obj-obj").attr({ "data-obj-id": sObjId }).html(CLJ[sObjId].sName).appendTo(jRow);
																		$(document.createElement("td")).addClass("cl-stats-obj-score").attr({ "data-obj-id": sObjId }).appendTo(jRow);
																	}
																	if(CLJ[sObjId].oSrc[sId]!=null)
																	{
																		jRow.children(".cl-stats-obj-score").html(CLJ[sObjId].oSrc[sId].nRawScore);
																	}
																	else
																	{
																		jRow.children(".cl-stats-obj-score").html("0");
																	}
																}
															}
															else
															{
																jQBlock.find(".cl-stats-right").hide();
															}
														}
														if(oCurObj.data.bDisplayVariants)
														{
															var jVarTable = jQBlock.find(".cl-results-q-variants-table");
															if(jVarTable.length!=0)
															{
																jVarTable.remove();
															}
															jVarTable = $(document.createElement("table")).addClass("cl-results-q-variants-table").appendTo(jQBlock.find(".cl-results-variants .cl-results-contents"));
															jVarTable = $(document.createElement("tbody")).appendTo(jVarTable);
															fnFillVars({ sId: sId, sType: sType, jVarTable: jVarTable, jxParams: jxParams, oStore: CLZ.oStore[sId] });
														}
														break;
													}
													case "i":
													{
														jQBlock.find(".cl-results-na").html("Не закончен");
														break;
													}
												}
											}
										}
									}
								}
							});
						}
						break;
					}
				}
				return true;
			}
